import{_ as s,c as n,o as t,a7 as e}from"./chunks/framework.BQb8NfN9.js";const h=JSON.parse('{"title":"机器学习算法分类","description":"","frontmatter":{},"headers":[],"relativePath":"ai/机器学习/04.机器学习算法分类.md","filePath":"ai/机器学习/04.机器学习算法分类.md","lastUpdated":1770393495000}'),l={name:"ai/机器学习/04.机器学习算法分类.md"};function r(p,a,d,i,o,c){return t(),n("div",null,a[0]||(a[0]=[e(`<h1 id="机器学习算法分类" tabindex="-1">机器学习算法分类 <a class="header-anchor" href="#机器学习算法分类" aria-label="Permalink to &quot;机器学习算法分类&quot;">​</a></h1><blockquote><p>本章介绍机器学习的四大学习范式：监督学习、无监督学习、半监督学习、强化学习</p></blockquote><hr><h2 id="一、四大学习范式概览" tabindex="-1">一、四大学习范式概览 <a class="header-anchor" href="#一、四大学习范式概览" aria-label="Permalink to &quot;一、四大学习范式概览&quot;">​</a></h2><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌────────────────────────────────────────────────────────────────┐</span></span>
<span class="line"><span>│                    机器学习算法分类                              │</span></span>
<span class="line"><span>├────────────────┬────────────────┬────────────────┬─────────────┤</span></span>
<span class="line"><span>│   监督学习      │   无监督学习    │   半监督学习    │   强化学习   │</span></span>
<span class="line"><span>│  Supervised    │  Unsupervised  │ Semi-Supervised│ Reinforcement│</span></span>
<span class="line"><span>├────────────────┼────────────────┼────────────────┼─────────────┤</span></span>
<span class="line"><span>│  ✅ 有标签     │  ❌ 无标签      │  🔄 部分标签    │  🎮 交互反馈 │</span></span>
<span class="line"><span>│  分类/回归     │  聚类          │  标签传播       │  策略优化    │</span></span>
<span class="line"><span>└────────────────┴────────────────┴────────────────┴─────────────┘</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><hr><h2 id="二、监督学习-supervised-learning" tabindex="-1">二、监督学习（Supervised Learning） <a class="header-anchor" href="#二、监督学习-supervised-learning" aria-label="Permalink to &quot;二、监督学习（Supervised Learning）&quot;">​</a></h2><h3 id="_2-1-核心特征" tabindex="-1">2.1 核心特征 <a class="header-anchor" href="#_2-1-核心特征" aria-label="Permalink to &quot;2.1 核心特征&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">定义</p><p><strong>监督学习</strong>：输入数据由特征值（X）和目标值（y）组成，训练数据必须带有标签。</p></div><p><strong>关键要素：</strong></p><ul><li>✅ 有标签数据</li><li>✅ 有反馈机制（模型知道预测对错）</li><li>✅ 需要&quot;监督者&quot;提供明确的标签指导</li></ul><h3 id="_2-2-两大子类" tabindex="-1">2.2 两大子类 <a class="header-anchor" href="#_2-2-两大子类" aria-label="Permalink to &quot;2.2 两大子类&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>监督学习</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    ├── 📈 回归 (Regression)</span></span>
<span class="line"><span>    │       └── 目标值为连续值</span></span>
<span class="line"><span>    │       └── 例：房价预测、温度预测</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    └── 🏷️ 分类 (Classification)</span></span>
<span class="line"><span>            └── 目标值为离散值</span></span>
<span class="line"><span>            └── 例：猫狗分类、垃圾邮件识别</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><table tabindex="0"><thead><tr><th>类型</th><th>目标值</th><th>典型任务</th><th>评估指标</th></tr></thead><tbody><tr><td><strong>回归</strong></td><td>连续值（如 15000, 18.5）</td><td>房价预测、销量预测</td><td>MSE, RMSE, MAE</td></tr><tr><td><strong>分类</strong></td><td>离散值（如 猫/狗, 0/1）</td><td>图像分类、情感分析</td><td>准确率、F1-Score</td></tr></tbody></table><h3 id="_2-3-典型示例-电影分类" tabindex="-1">2.3 典型示例：电影分类 <a class="header-anchor" href="#_2-3-典型示例-电影分类" aria-label="Permalink to &quot;2.3 典型示例：电影分类&quot;">​</a></h3><table tabindex="0"><thead><tr><th>电影</th><th>搞笑镜头</th><th>拥抱镜头</th><th>打斗镜头</th><th>类型（标签）</th></tr></thead><tbody><tr><td>功夫熊猫</td><td>45</td><td>12</td><td>30</td><td>喜剧片</td></tr><tr><td>泰坦尼克</td><td>5</td><td>50</td><td>8</td><td>爱情片</td></tr><tr><td>叶问</td><td>10</td><td>8</td><td>80</td><td>动作片</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">易混淆点</p><p>区分分类与回归的依据是<strong>目标值类型</strong>，而非特征！</p></div><hr><h2 id="三、无监督学习-unsupervised-learning" tabindex="-1">三、无监督学习（Unsupervised Learning） <a class="header-anchor" href="#三、无监督学习-unsupervised-learning" aria-label="Permalink to &quot;三、无监督学习（Unsupervised Learning）&quot;">​</a></h2><h3 id="_3-1-核心特征" tabindex="-1">3.1 核心特征 <a class="header-anchor" href="#_3-1-核心特征" aria-label="Permalink to &quot;3.1 核心特征&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">定义</p><p><strong>无监督学习</strong>：输入数据无任何标签，通过样本间相似性进行分组，发现数据内部结构。</p></div><p><strong>关键要素：</strong></p><ul><li>❌ 无标签数据</li><li>❌ 无反馈机制</li><li>✅ 基于相似性自动聚类</li></ul><h3 id="_3-2-聚类示意" tabindex="-1">3.2 聚类示意 <a class="header-anchor" href="#_3-2-聚类示意" aria-label="Permalink to &quot;3.2 聚类示意&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>原始数据（无标签）          聚类后</span></span>
<span class="line"><span>    ●  ▲  ■                 ┌─────────┐</span></span>
<span class="line"><span>    ●  ▲  ■     ────►       │ ● ● ●  │ 类别 A</span></span>
<span class="line"><span>    ●  ▲  ■                 ├─────────┤</span></span>
<span class="line"><span>    ●  ▲  ■                 │ ▲ ▲ ▲  │ 类别 B</span></span>
<span class="line"><span>                            ├─────────┤</span></span>
<span class="line"><span>                            │ ■ ■ ■  │ 类别 C</span></span>
<span class="line"><span>                            └─────────┘</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h3 id="_3-3-特点" tabindex="-1">3.3 特点 <a class="header-anchor" href="#_3-3-特点" aria-label="Permalink to &quot;3.3 特点&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特点</th><th>说明</th></tr></thead><tbody><tr><td>分类自由</td><td>可根据需求灵活确定类别数量</td></tr><tr><td>无标准答案</td><td>聚类结果依赖主观定义</td></tr><tr><td>发现结构</td><td>揭示数据内在规律</td></tr></tbody></table><details class="details custom-block"><summary>💡 典型应用</summary><ul><li>用户分群（电商用户画像）</li><li>图像分割</li><li>异常检测</li><li>新闻话题聚类</li></ul></details><hr><h2 id="四、半监督学习-semi-supervised-learning" tabindex="-1">四、半监督学习（Semi-Supervised Learning） <a class="header-anchor" href="#四、半监督学习-semi-supervised-learning" aria-label="Permalink to &quot;四、半监督学习（Semi-Supervised Learning）&quot;">​</a></h2><h3 id="_4-1-核心特征" tabindex="-1">4.1 核心特征 <a class="header-anchor" href="#_4-1-核心特征" aria-label="Permalink to &quot;4.1 核心特征&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">定义</p><p><strong>半监督学习</strong>：结合少量标注数据和大量未标注数据进行训练。</p></div><h3 id="_4-2-工作流程" tabindex="-1">4.2 工作流程 <a class="header-anchor" href="#_4-2-工作流程" aria-label="Permalink to &quot;4.2 工作流程&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────────────────────────────────┐</span></span>
<span class="line"><span>│                     半监督学习流程                           │</span></span>
<span class="line"><span>└─────────────────────────────────────────────────────────────┘</span></span>
<span class="line"><span>         │</span></span>
<span class="line"><span>         ▼</span></span>
<span class="line"><span>┌─────────────────┐    ┌─────────────────┐</span></span>
<span class="line"><span>│ 1️⃣ 少量标注数据  │ →  │ 2️⃣ 训练初始模型  │</span></span>
<span class="line"><span>│   (1万条)        │    │                 │</span></span>
<span class="line"><span>└─────────────────┘    └────────┬────────┘</span></span>
<span class="line"><span>                                │</span></span>
<span class="line"><span>                                ▼</span></span>
<span class="line"><span>┌─────────────────┐    ┌─────────────────┐</span></span>
<span class="line"><span>│ 4️⃣ 人工校正结果  │ ←  │ 3️⃣ 预测未标注数据 │</span></span>
<span class="line"><span>│   (修正错误)     │    │   (99万条)       │</span></span>
<span class="line"><span>└─────────────────┘    └─────────────────┘</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br></div></div><h3 id="_4-3-核心优势" tabindex="-1">4.3 核心优势 <a class="header-anchor" href="#_4-3-核心优势" aria-label="Permalink to &quot;4.3 核心优势&quot;">​</a></h3><table tabindex="0"><thead><tr><th>优势</th><th>说明</th></tr></thead><tbody><tr><td>💰 降低成本</td><td>大幅减少人工标注工作量</td></tr><tr><td>⚡ 提高效率</td><td>用少量标注数据撬动大量数据</td></tr><tr><td>📊 适用场景</td><td>标注成本高但数据量大的任务</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">典型应用</p><p>数据标注领域：处理百万级数据集时，先用 1 万标注数据训练模型，再预测剩余 99 万数据。</p></div><hr><h2 id="五、强化学习-reinforcement-learning" tabindex="-1">五、强化学习（Reinforcement Learning） <a class="header-anchor" href="#五、强化学习-reinforcement-learning" aria-label="Permalink to &quot;五、强化学习（Reinforcement Learning）&quot;">​</a></h2><h3 id="_5-1-核心特征" tabindex="-1">5.1 核心特征 <a class="header-anchor" href="#_5-1-核心特征" aria-label="Permalink to &quot;5.1 核心特征&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">定义</p><p><strong>强化学习</strong>：智能体通过与环境交互，根据奖励信号优化决策策略。</p></div><h3 id="_5-2-四大核心要素" tabindex="-1">5.2 四大核心要素 <a class="header-anchor" href="#_5-2-四大核心要素" aria-label="Permalink to &quot;5.2 四大核心要素&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>        ┌─────────────────────────────────────┐</span></span>
<span class="line"><span>        │           🌍 环境 (Environment)      │</span></span>
<span class="line"><span>        └─────────────────────────────────────┘</span></span>
<span class="line"><span>                    ▲           │</span></span>
<span class="line"><span>          State     │           │  Reward</span></span>
<span class="line"><span>          (状态)    │           │  (奖励)</span></span>
<span class="line"><span>                    │           ▼</span></span>
<span class="line"><span>        ┌─────────────────────────────────────┐</span></span>
<span class="line"><span>        │          🤖 智能体 (Agent)           │</span></span>
<span class="line"><span>        │                                     │</span></span>
<span class="line"><span>        │         选择 Action (行动)           │</span></span>
<span class="line"><span>        └─────────────────────────────────────┘</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br></div></div><table tabindex="0"><thead><tr><th>要素</th><th>英文</th><th>说明</th></tr></thead><tbody><tr><td>🤖 <strong>智能体</strong></td><td>Agent</td><td>执行决策的主体</td></tr><tr><td>🌍 <strong>状态</strong></td><td>State</td><td>当前环境状况</td></tr><tr><td>🎯 <strong>行动</strong></td><td>Action</td><td>可执行的操作</td></tr><tr><td>🏆 <strong>奖励</strong></td><td>Reward</td><td>行动后的反馈</td></tr></tbody></table><h3 id="_5-3-学习机制" tabindex="-1">5.3 学习机制 <a class="header-anchor" href="#_5-3-学习机制" aria-label="Permalink to &quot;5.3 学习机制&quot;">​</a></h3><p><strong>核心目标：</strong> 通过<strong>奖励最大化</strong>驱动智能体优化策略</p><details class="details custom-block"><summary>💡 生活示例</summary><p><strong>儿童学走路：</strong></p><ul><li>智能体：儿童</li><li>状态：当前姿势、位置</li><li>行动：迈步、保持平衡</li><li>奖励：成功走一步 → 巧克力 🍫</li></ul></details><h3 id="_5-4-典型案例" tabindex="-1">5.4 典型案例 <a class="header-anchor" href="#_5-4-典型案例" aria-label="Permalink to &quot;5.4 典型案例&quot;">​</a></h3><table tabindex="0"><thead><tr><th>案例</th><th>智能体</th><th>奖励机制</th></tr></thead><tbody><tr><td>AlphaGo</td><td>棋手程序</td><td>胜利 +1，失败 -1</td></tr><tr><td>无人驾驶</td><td>自动驾驶系统</td><td>安全行驶奖励，碰撞惩罚</td></tr><tr><td>游戏 NPC</td><td>虚拟角色</td><td>完成任务奖励</td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">特点区分</p><p>强化学习能<strong>实时</strong>根据环境状态调整策略，区别于静态的历史数据学习。</p></div><hr><h2 id="六、四大范式对比" tabindex="-1">六、四大范式对比 <a class="header-anchor" href="#六、四大范式对比" aria-label="Permalink to &quot;六、四大范式对比&quot;">​</a></h2><table tabindex="0"><thead><tr><th>维度</th><th>监督学习</th><th>无监督学习</th><th>半监督学习</th><th>强化学习</th></tr></thead><tbody><tr><td><strong>标签</strong></td><td>✅ 全部有</td><td>❌ 全部无</td><td>🔄 部分有</td><td>🎮 动态奖励</td></tr><tr><td><strong>反馈</strong></td><td>即时反馈</td><td>无反馈</td><td>部分反馈</td><td>延迟反馈</td></tr><tr><td><strong>目标</strong></td><td>预测标签</td><td>发现结构</td><td>标签传播</td><td>策略优化</td></tr><tr><td><strong>典型任务</strong></td><td>分类、回归</td><td>聚类</td><td>数据标注</td><td>决策控制</td></tr><tr><td><strong>典型案例</strong></td><td>图像分类</td><td>用户分群</td><td>大规模标注</td><td>AlphaGo</td></tr></tbody></table><hr><h2 id="七、知识小结" tabindex="-1">七、知识小结 <a class="header-anchor" href="#七、知识小结" aria-label="Permalink to &quot;七、知识小结&quot;">​</a></h2><table tabindex="0"><thead><tr><th>知识点</th><th>核心内容</th><th>考试重点/易混淆点</th><th>难度</th></tr></thead><tbody><tr><td>算法分类</td><td>四大类：监督、无监督、半监督、强化</td><td>区分标准：目标值是否存在及类型</td><td>⭐⭐</td></tr><tr><td>监督学习</td><td>有标签，分为回归（连续）和分类（离散）</td><td>分类与回归的区分依据是目标值，而非特征</td><td>⭐⭐⭐</td></tr><tr><td>无监督学习</td><td>无标签，依赖样本相似度聚类</td><td>聚类结果无标准答案，依赖主观定义</td><td>⭐⭐</td></tr><tr><td>半监督学习</td><td>混合标记与未标记数据</td><td>优势：降低标注成本，提升效率</td><td>⭐⭐⭐⭐</td></tr><tr><td>强化学习</td><td>四要素：Agent、State、Action、Reward</td><td>核心：实时交互与动态奖励反馈</td><td>⭐⭐⭐⭐⭐</td></tr><tr><td>面试逻辑</td><td>分类框架 → 每类定义 → 子类 → 示例</td><td>重点：层次清晰，结合实例</td><td>⭐⭐⭐</td></tr></tbody></table><hr><h2 id="八、面试高频问题" tabindex="-1">八、面试高频问题 <a class="header-anchor" href="#八、面试高频问题" aria-label="Permalink to &quot;八、面试高频问题&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">💬 常见面试题</p><p><strong>Q: 请介绍机器学习算法的主要分类及其特点？</strong></p><p><strong>A:</strong> 机器学习算法主要分为四类：</p><ol><li><p><strong>监督学习</strong>：有标签数据，分为回归（目标值连续，如房价预测）和分类（目标值离散，如猫狗分类）</p></li><li><p><strong>无监督学习</strong>：无标签数据，通过相似性进行聚类，如用户分群</p></li><li><p><strong>半监督学习</strong>：部分有标签，常用于大规模数据标注场景，降低标注成本</p></li><li><p><strong>强化学习</strong>：通过环境交互获得奖励反馈，优化决策策略。四要素为 Agent（智能体）、State（状态）、Action（行动）、Reward（奖励）。典型案例如 AlphaGo。</p></li></ol></div>`,59)]))}const u=s(l,[["render",r]]);export{h as __pageData,u as default};
