import{_ as a,c as r,o as l,a8 as i}from"./chunks/framework.B1-gFi6y.js";const o="/Lyc-notes/assets/%E8%8A%B1%E7%93%A3%E6%8E%A8%E8%8D%90%E7%AD%96%E7%95%A5%E6%A1%86%E6%9E%B6.C5g6d_w7.png",e="/Lyc-notes/assets/%E5%8F%91%E7%8E%B0%E9%A1%B5%E6%8E%A8%E8%8D%90%E6%B5%81%E7%A8%8B%E5%9B%BE.jnTqt9E1.png",d="/Lyc-notes/assets/%E7%9B%B8%E4%BC%BC%E5%95%86%E7%94%A8%E7%B4%A0%E6%9D%90.DTgkWbR5.png",n="/Lyc-notes/assets/%E4%B8%BA%E4%BD%A0%E6%8E%A8%E8%8D%90.4GMofqgA.png",s="/Lyc-notes/assets/%E7%9B%B8%E4%BC%BC%E9%87%87%E9%9B%86.DRcv1C17.png",_=JSON.parse('{"title":"花瓣推荐能力介绍","description":"","frontmatter":{},"headers":[],"relativePath":"project/gaoding/花瓣推荐能力介绍/page.md","filePath":"project/gaoding/花瓣推荐能力介绍/page.md","lastUpdated":1768577009000}'),h={name:"project/gaoding/花瓣推荐能力介绍/page.md"};function g(u,t,b,p,c,m){return l(),r("div",null,t[0]||(t[0]=[i('<h1 id="花瓣推荐能力介绍" tabindex="-1">花瓣推荐能力介绍 <a class="header-anchor" href="#花瓣推荐能力介绍" aria-label="Permalink to &quot;花瓣推荐能力介绍&quot;">​</a></h1><h2 id="_1-推荐能力框架图" tabindex="-1">1. 推荐能力框架图 <a class="header-anchor" href="#_1-推荐能力框架图" aria-label="Permalink to &quot;1. 推荐能力框架图&quot;">​</a></h2><p><img src="'+o+'" alt="推荐能力框架图"></p><h2 id="_2-推荐能力详细介绍" tabindex="-1">2. 推荐能力详细介绍 <a class="header-anchor" href="#_2-推荐能力详细介绍" aria-label="Permalink to &quot;2. 推荐能力详细介绍&quot;">​</a></h2><h3 id="_2-1-发现页" tabindex="-1">2.1 发现页 <a class="header-anchor" href="#_2-1-发现页" aria-label="Permalink to &quot;2.1 发现页&quot;">​</a></h3><p><img src="'+e+'" alt="发现页推荐流程图"></p><h4 id="内容基础" tabindex="-1">内容基础 <a class="header-anchor" href="#内容基础" aria-label="Permalink to &quot;内容基础&quot;">​</a></h4><ul><li><strong>阶段划分</strong>：冷启、稳态、淘汰阶段</li><li><strong>相关优化</strong>： <ul><li>20250721 发现页增加阶梯式分发策略一期（需ab）</li><li>20250928 发现页增加阶梯式分发策略二期-优化当前晋级淘汰规则及优先保障近期新内容分发的比例（需ab）</li></ul></li><li><strong>赛马三期</strong>：<a href="https://alidocs.dingtalk.com/i/nodes/P7QG4Yx2Jp7xZ4GZh1ZZdGB7V9dEq3XD" target="_blank" rel="noreferrer">详细文档</a></li></ul><h4 id="过滤" tabindex="-1">过滤 <a class="header-anchor" href="#过滤" aria-label="Permalink to &quot;过滤&quot;">​</a></h4><p><strong>全局过滤逻辑</strong>：</p><ul><li>状态为淘汰</li><li>敏感标签（美女等）</li><li>AIGC内容</li><li>长图内容</li><li>过气内容</li><li>下架内容</li></ul><p><strong>个性化过滤逻辑</strong>：</p><ul><li>用户采集过、下载过内容</li><li>近3天曝光内容</li><li>近7天曝光过次数大于2次的</li><li>负反馈过滤及其相似等的过滤</li></ul><p><strong>特殊规则</strong>：</p><ul><li>请求结果数在1000以后，每个召回路过滤前置推荐给用户的1000结果里的内容，再次重走链路</li><li>AI社区内容：实时过滤累计屏蔽&amp;举报大于等于2的内容</li><li>过滤用户屏蔽举报向量的相似内容</li><li>20250822 发现页top1000以后的内容增加个性化逻辑（需ab）</li></ul><h4 id="召回" tabindex="-1">召回 <a class="header-anchor" href="#召回" aria-label="Permalink to &quot;召回&quot;">​</a></h4><p><strong>召回策略</strong>：</p><table tabindex="0"><thead><tr><th>召回类型</th><th>说明</th><th>相关优化</th></tr></thead><tbody><tr><td>冷启-实时多目标</td><td>近7天，7-2个月，2个月后</td><td>20250623 发现页召回离线dssm替换为实时多目标召回（需ab）</td></tr><tr><td>稳态-实时多目标</td><td>-</td><td>-</td></tr><tr><td>稳态-实时多目标common</td><td>-</td><td>-</td></tr><tr><td>近期i2i</td><td><a href="https://alidocs.dingtalk.com/i/nodes/YQBnd5ExVEwjpePpCbdLXevg8yeZqMmz?corpId=" target="_blank" rel="noreferrer">详细文档</a></td><td>-</td></tr><tr><td>关注链召回</td><td>-</td><td>20250701 发现页关注链召回优化&amp;前端交互优化&amp;默认跳转逻辑优化（需ab）</td></tr><tr><td>热度召回/补余</td><td>-</td><td>-</td></tr><tr><td>业务池召回</td><td>随机召回</td><td>-</td></tr><tr><td>扶持分重排</td><td>-</td><td>20250521 发现页i2i召回优化及增加扶持分应用（需ab）<br>20250721 发现页增加阶梯式分发策略一期（需ab）</td></tr></tbody></table><h4 id="精排" tabindex="-1">精排 <a class="header-anchor" href="#精排" aria-label="Permalink to &quot;精排&quot;">​</a></h4><ul><li><strong>多目标</strong>：点击、采集、下载</li><li><strong>模型</strong>：花瓣发现页(推荐)多目标精排模型v4</li><li><strong>相关优化</strong>：20251009 发现页排序模型优化——增加用户长周期序列行为（需ab）</li></ul><h4 id="重排" tabindex="-1">重排 <a class="header-anchor" href="#重排" aria-label="Permalink to &quot;重排&quot;">​</a></h4><p><strong>L4分</strong>：</p><ul><li>包含点击、采集、下载、扶持分，进行计算</li><li>相关优化：20250521 发现页i2i召回优化及增加扶持分应用（需ab）</li></ul><p><strong>混排比例</strong>：</p><ul><li>素材关注链召回的混排比例固定为5%</li><li>素材冷启阶段-实时多目标5%</li><li>灵感冷启阶段近7天-实时多目标5%</li><li>冷启阶段7-2个月-实时多目标5%</li><li>冷启阶段2个月前-实时多目标5%</li><li>素材其他召回路（除去前面说的两路）召回的为15%</li><li>其他灵感算法池兜底</li></ul><p><strong>针对每桶的限制</strong>：</p><ol><li>近1个小时行为i2i保障在每一桶内的前五个内容里至少有一个</li><li>非冷启用户，灵感关注链保障在每一桶内至少有一个内容 <ul><li><a href="https://alidocs.dingtalk.com/i/nodes/7NkDwLng8ZMaB7ZBCeNRnbLNJKMEvZBY" target="_blank" rel="noreferrer">详细文档</a></li></ul></li></ol><p><strong>同源限制</strong>：</p><ul><li>每40个相邻内容（滑动窗口）中同一个用户不得超过了3个，若超过则顺延往后排。该策略只对灵感生效，素材内容暂不做该逻辑限制。</li><li>每40个相邻内容（滑动窗口）中关注链内容来自同一个画板/来源不得超过了3个，若超过则顺延往后排。该策略只对灵感生效，素材内容暂不做该逻辑限制。</li><li>相关优化：20250701 发现页关注链召回优化&amp;前端交互优化&amp;默认跳转逻辑优化（需ab）</li></ul><h4 id="刷新规则" tabindex="-1">刷新规则 <a class="header-anchor" href="#刷新规则" aria-label="Permalink to &quot;刷新规则&quot;">​</a></h4><p><strong>刷新后取结果规则</strong>：</p><ul><li>每次刷新取下一页结果</li><li>第二天重置重新从第一页开始请求</li></ul><p><strong>当天刷新规则</strong>：</p><ul><li>当用户有实时i2i变更或刷到1000以后或距离上次请求超过10分钟，重新请求接口</li><li>过滤之前推荐的内容（按后端请求过给前端展示的，不是按真实曝光的），再推出1000个</li></ul><blockquote><p><strong>待完善</strong>：[WIP]发现页无限推荐详细设计</p></blockquote><hr><h3 id="_2-2-基于画板的灵感推荐" tabindex="-1">2.2 基于画板的灵感推荐 <a class="header-anchor" href="#_2-2-基于画板的灵感推荐" aria-label="Permalink to &quot;2.2 基于画板的灵感推荐&quot;">​</a></h3><h4 id="推荐范围" tabindex="-1">推荐范围 <a class="header-anchor" href="#推荐范围" aria-label="Permalink to &quot;推荐范围&quot;">​</a></h4><p>需推荐灵感的画板范围：<strong>包含所有画板</strong></p><h4 id="推荐逻辑" tabindex="-1">推荐逻辑 <a class="header-anchor" href="#推荐逻辑" aria-label="Permalink to &quot;推荐逻辑&quot;">​</a></h4><h5 id="_1-推荐池范围" tabindex="-1">1. 推荐池范围 <a class="header-anchor" href="#_1-推荐池范围" aria-label="Permalink to &quot;1. 推荐池范围&quot;">​</a></h5><p>仅推荐灵感即可</p><h5 id="_2-召回" tabindex="-1">2. 召回 <a class="header-anchor" href="#_2-召回" aria-label="Permalink to &quot;2. 召回&quot;">​</a></h5><p><strong>召回流程</strong>：</p><ol><li>取画板内对应采集的file_id，取按采集时间最近的100个采集对应file_id的灵感i2v结果的top80进行返回（取web端当前的结果即可）</li><li>过滤画板中采集的近500个采集</li><li>按照原画板内取的这100个采集内容的采集时间由近到远，取对应每个采集内容的相似内容按照1:1:1的逻辑穿插排序，边排序边去重</li><li>统计用户近3天（不含当天）在发现页和画板灵感推荐模块的该画板推荐曝光过的内容，穿插排序好的内容对这部分曝光过内容的沉底，再按照顺序截断取top2000</li><li><strong>补余逻辑</strong>（若取的数量不足2000）： <ul><li>通过搜索接口（1.5改的逻辑），在画板的分类的范围内用画板名称进行搜索，根据搜索结果返回的顺序补足至2000</li><li>若还是不足2000，则去掉画板分类限制进行搜索，根据搜索结果返回的顺序补足至2000（此时不限制是否是灵感）</li><li>同样需要进行多取，需过滤掉在该模块曝光过内容</li></ul></li></ol><h5 id="_3-排序" tabindex="-1">3. 排序 <a class="header-anchor" href="#_3-排序" aria-label="Permalink to &quot;3. 排序&quot;">​</a></h5><p>调取发现页ctr接口，根据ctr进行排序</p><h5 id="_4-重排" tabindex="-1">4. 重排 <a class="header-anchor" href="#_4-重排" aria-label="Permalink to &quot;4. 重排&quot;">​</a></h5><p><strong>分档策略</strong>（按优先级排序：一档 &gt; 二档 &gt; 三档 &gt; 四档 &gt; 五档）：</p><table tabindex="0"><thead><tr><th>档位</th><th>说明</th><th>策略</th></tr></thead><tbody><tr><td><strong>第一档</strong>（最近采的1个像的）</td><td>取近3天（含今天）内用户在该画板最新采集的1个内容，取这个内容在召回最终结果（2000）里根据i2i召回的相似内容，过滤近3天在这个模块被曝光过的内容，按精排排序取靠前的5个结果作为一档结果</td><td>档内根据精排分排</td></tr><tr><td><strong>第二档</strong>（最近采的像的）</td><td>取近3天（含今天）内用户在该画板采集的内容（也含第一档取的采集），取这些内容在召回最终结果里根据i2i召回的相似内容，过滤近3天在这个模块被曝光过的相似内容，过滤第一档取过的内容，限制每个内容根据精排分最多取top10个相似结果</td><td>这些相似结果再一起根据精排得分进行排序，截断40个作为二档结果</td></tr><tr><td><strong>第三档</strong>（采集的像的）</td><td>取所有采集内容在召回最终结果里根据i2i召回的相似内容，过滤近3天在这个模块被曝光过的相似内容，过滤第一、二档取过的内容</td><td>根据精排得分进行混排，作为三档结果</td></tr><tr><td><strong>第四档</strong>（采集的像的且曝光过的）</td><td>取召回2000内容里当前模块近3天曝光过的相似内容</td><td>档内根据精排排——解决极端情况下只有第四档和第五档，第四档需要优于第五档</td></tr><tr><td><strong>第五档</strong>（补余）</td><td>补余的内容</td><td>档内根据精排分排</td></tr></tbody></table><hr><h3 id="_2-3-关联推荐-为你推荐-、相似素材推荐" tabindex="-1">2.3 关联推荐（为你推荐）、相似素材推荐 <a class="header-anchor" href="#_2-3-关联推荐-为你推荐-、相似素材推荐" aria-label="Permalink to &quot;2.3 关联推荐（为你推荐）、相似素材推荐&quot;">​</a></h3><h4 id="_2-3-1-相似素材推荐" tabindex="-1">2.3.1 相似素材推荐 <a class="header-anchor" href="#_2-3-1-相似素材推荐" aria-label="Permalink to &quot;2.3.1 相似素材推荐&quot;">​</a></h4><p><img src="'+d+'" alt="相似素材推荐流程图"></p><h5 id="过滤-1" tabindex="-1">过滤 <a class="header-anchor" href="#过滤-1" aria-label="Permalink to &quot;过滤&quot;">​</a></h5><ul><li>过滤拍信内容</li><li>过滤源内容</li><li>部分场景下业务方会带筛选器进行请求（部分类型用户不支持部分类型的内容，比如大企业用户等）</li><li>实时过滤由于审核&amp;屏蔽状态被淘汰内容</li><li><strong>相关优化</strong>： <ul><li>20250514 花瓣相似商用素材过滤拍信内容</li><li>相似商用素材-支持针对特殊用户限制召回范围</li></ul></li></ul><h5 id="召回-1" tabindex="-1">召回 <a class="header-anchor" href="#召回-1" aria-label="Permalink to &quot;召回&quot;">​</a></h5><p><strong>召回策略</strong>：</p><table tabindex="0"><thead><tr><th>召回类型</th><th>说明</th><th>相关优化</th></tr></thead><tbody><tr><td><strong>素材-i2v</strong></td><td>-</td><td>-</td></tr><tr><td><strong>素材-实时多目标召回</strong></td><td>取（源内容id+用户id）进行实时多模态向量生成，根据生成的向量，与算法提供的素材向量推荐池进行匹配，实时计算相似度<br>传入该用户当天的近5个搜索词及近16个点击或采集对应的fileid</td><td>基于实时特征的多目标召回（需ab）</td></tr><tr><td><strong>素材-图搜召回</strong></td><td>候选集：按照热度分，素材候选集扩充到2亿，灵感候选集扩充到2亿（不受热度分阈值限制）。后续每天增量更新（增量更新规则：取近x天创建的内容且可被搜索/上架状态，按热度分排取topx）<br>召回时有召回阈值</td><td>20250414 花瓣相似采集&amp;相似素材推荐图搜路召回模型升级（需ab）<br>20240426 相似商用素材-花瓣素材ai图像相似缓存结果增加更新规则（切换新图搜后暂停了）</td></tr><tr><td><strong>素材-同在画板</strong></td><td>具体策略</td><td>-</td></tr></tbody></table><h5 id="精排-和为你推荐共用模型" tabindex="-1">精排（和为你推荐共用模型） <a class="header-anchor" href="#精排-和为你推荐共用模型" aria-label="Permalink to &quot;精排（和为你推荐共用模型）&quot;">​</a></h5><ul><li><strong>多目标</strong>：包含点击（和采集）和下载</li><li><strong>特殊场景</strong>：若无fileid（图搜场景点击进入相似素材tab），或带有fileid但是同时带有内容范围筛选器（比如大企业用户场景），则仅进行图搜召回，不过ctr模型，根据相似度的分值进行排序</li><li><strong>支持特征</strong>： <ul><li>支持搜索、点击采集的实时特征</li><li>支持当前搜索词、近一个跳转内容的实时上下文特征</li></ul></li><li><strong>相关优化</strong>： <ul><li>20240305-花瓣-相似商用素材排序模型优化&amp;排序分优化（需ab）</li><li>20240109-花瓣-商用素材特殊用户ai图搜召回链路优化及对部分特殊用户相似商用素材推荐不过ctr模型</li><li>20250826 花瓣采集详情页-为你推荐&amp;相似商用素材增加排序优化及缓存优化（需ab）</li><li>花瓣详情页为你推荐&amp;相似素材模块排序增加实时上下文特征（需ab）</li></ul></li></ul><h5 id="重排-1" tabindex="-1">重排 <a class="header-anchor" href="#重排-1" aria-label="Permalink to &quot;重排&quot;">​</a></h5><ul><li><strong>会员老客对视觉中国降权</strong><ul><li>相关优化：20241028 花瓣-相似商用素材模块针对会员老客对视觉中国素材进行降权（需ab）</li></ul></li></ul><h5 id="特殊处理" tabindex="-1">特殊处理 <a class="header-anchor" href="#特殊处理" aria-label="Permalink to &quot;特殊处理&quot;">​</a></h5><ul><li>当前视频和gif图业务前端未展示相似素材模块</li></ul><hr><h4 id="_2-3-2-为你推荐" tabindex="-1">2.3.2 为你推荐 <a class="header-anchor" href="#_2-3-2-为你推荐" aria-label="Permalink to &quot;2.3.2 为你推荐&quot;">​</a></h4><p><img src="'+n+'" alt="为你推荐流程图"></p><h5 id="过滤-2" tabindex="-1">过滤 <a class="header-anchor" href="#过滤-2" aria-label="Permalink to &quot;过滤&quot;">​</a></h5><p>实时过滤由于审核&amp;屏蔽状态被淘汰内容</p><h5 id="召回-2" tabindex="-1">召回 <a class="header-anchor" href="#召回-2" aria-label="Permalink to &quot;召回&quot;">​</a></h5><p><strong>召回策略</strong>：</p><table tabindex="0"><thead><tr><th>召回类型</th><th>说明</th></tr></thead><tbody><tr><td><strong>灵感-图搜召回</strong></td><td>同上素材-图搜</td></tr><tr><td><strong>灵感-i2v</strong></td><td>-</td></tr><tr><td><strong>灵感-同在画板</strong></td><td>同上素材-同在画板</td></tr><tr><td><strong>灵感-多目标召回</strong></td><td>同上素材-多目标召回</td></tr><tr><td><strong>素材召回</strong></td><td>取相似素材最终排序中的21-200的结果</td></tr><tr><td><strong>灵感拓展召回</strong>（140后请求）</td><td>灵感i2v召回：取灵感的i2v召回800个结果，去除精准内容召回的内容，剩余所有i2v内容，作为拓展召回的结果<br>灵感-同在画板召回：去除需要高于源内容平均同在画板数1.5倍的要求，取灵感的同在画板召回top300，去除掉在精准召回时已经召回的内容，作为拓展结果<br>相关优化：20250820 花瓣采集详情页-为你推荐模块中尾部插入拓展类内容及为你推荐和相似素材增加同在画板召回（需ab）</td></tr></tbody></table><h5 id="精排-1" tabindex="-1">精排 <a class="header-anchor" href="#精排-1" aria-label="Permalink to &quot;精排&quot;">​</a></h5><p>同相似素材用一样的模型</p><h5 id="重排-2" tabindex="-1">重排 <a class="header-anchor" href="#重排-2" aria-label="Permalink to &quot;重排&quot;">​</a></h5><ul><li><strong>位置1-140</strong>：仅展示灵感&amp;素材的精准内容</li><li><strong>位置141开始</strong>：取剩余的精准内容和拓展内容，根据以下比例进行混排，档内根据精排分进行排序，最终截断800个内容 <ul><li>精准内容-素材算法池：20%</li><li>拓展内容-灵感算法池：10%</li><li>精准内容灵感算法池：70%</li></ul></li><li><strong>相关优化</strong>：20250820 花瓣采集详情页-为你推荐模块中尾部插入拓展类内容及为你推荐和相似素材增加同在画板召回（需ab）</li></ul><hr><h3 id="_2-4-主题页推荐-即有筛选器id限制-且无搜索词的搜索个推逻辑" tabindex="-1">2.4 主题页推荐（即有筛选器id限制，且无搜索词的搜索个推逻辑） <a class="header-anchor" href="#_2-4-主题页推荐-即有筛选器id限制-且无搜索词的搜索个推逻辑" aria-label="Permalink to &quot;2.4 主题页推荐（即有筛选器id限制，且无搜索词的搜索个推逻辑）&quot;">​</a></h3><p>筛选器范围限制 + 热度召回 + CTR排序（走无搜索词的搜索接口逻辑）</p><hr><h3 id="_2-5-图搜-相似采集-搜相似" tabindex="-1">2.5 图搜\\相似采集\\搜相似 <a class="header-anchor" href="#_2-5-图搜-相似采集-搜相似" aria-label="Permalink to &quot;2.5 图搜\\相似采集\\搜相似&quot;">​</a></h3><p><img src="'+s+'" alt="图搜似采集相似流程图"></p><p><strong>图搜候选集</strong>：</p><ul><li>按照热度分，素材候选集扩充到2亿，灵感候选集扩充到2亿（不受热度分阈值限制）</li><li>后续每天增量更新（增量更新规则：取近x天创建的内容且可被搜索/上架状态，按热度分排取topx）</li></ul>',85)]))}const f=a(h,[["render",g]]);export{_ as __pageData,f as default};
