import{_ as a,c as l,o as r,a7 as e}from"./chunks/framework.BQb8NfN9.js";const u=JSON.parse('{"title":"软件设计师 历年真题要点","description":"","frontmatter":{},"headers":[],"relativePath":"software-exam/软件设计师/真题/历年真题要点.md","filePath":"software-exam/软件设计师/真题/历年真题要点.md","lastUpdated":1770085161000}'),i={name:"software-exam/软件设计师/真题/历年真题要点.md"};function d(o,t,h,n,s,c){return r(),l("div",null,t[0]||(t[0]=[e('<h1 id="软件设计师-历年真题要点" tabindex="-1">软件设计师 历年真题要点 <a class="header-anchor" href="#软件设计师-历年真题要点" aria-label="Permalink to &quot;软件设计师 历年真题要点&quot;">​</a></h1><blockquote><p>本文档整理历年软件设计师考试中的典型题目和要点。</p></blockquote><hr><h2 id="一、2016年上半年上午题" tabindex="-1">一、2016年上半年上午题 <a class="header-anchor" href="#一、2016年上半年上午题" aria-label="Permalink to &quot;一、2016年上半年上午题&quot;">​</a></h2><h3 id="_1-ping命令诊断顺序" tabindex="-1">1. PING命令诊断顺序 <a class="header-anchor" href="#_1-ping命令诊断顺序" aria-label="Permalink to &quot;1. PING命令诊断顺序&quot;">​</a></h3><p>使用PING命令进行网络监测时，按照<strong>从近到远</strong>的顺序依次执行：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>PING 127.0.0.1 → PING 本地IP → PING 默认网关 → PING 远程主机</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><strong>诊断作用</strong>：</p><ul><li><code>127.0.0.1</code>：测试本地TCP/IP协议栈</li><li>本地IP：测试本地网卡配置</li><li>默认网关：测试到网关的连通性</li><li>远程主机：测试到目标的完整链路</li></ul><h3 id="_2-默认网关计算" tabindex="-1">2. 默认网关计算 <a class="header-anchor" href="#_2-默认网关计算" aria-label="Permalink to &quot;2. 默认网关计算&quot;">​</a></h3><p>默认网关通常是（但也可由用户指定）：</p><ul><li>网络中的<strong>第一个可用地址</strong>（网络ID + 1）</li><li>次常见情况：网络中的最后一个可用地址（广播地址 - 1）</li></ul><h3 id="_3-ftp协议" tabindex="-1">3. FTP协议 <a class="header-anchor" href="#_3-ftp协议" aria-label="Permalink to &quot;3. FTP协议&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特性</th><th>说明</th></tr></thead><tbody><tr><td>控制端口</td><td>21</td></tr><tr><td>数据端口</td><td>20（主动模式）</td></tr><tr><td>所属层次</td><td>应用层协议</td></tr><tr><td>传输层</td><td>基于TCP</td></tr></tbody></table><h3 id="_4-加密算法分类" tabindex="-1">4. 加密算法分类 <a class="header-anchor" href="#_4-加密算法分类" aria-label="Permalink to &quot;4. 加密算法分类&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>算法示例</th></tr></thead><tbody><tr><td>对称加密</td><td>AES, DES, 3DES</td></tr><tr><td>非对称加密</td><td>RSA, ECC</td></tr></tbody></table><hr><h2 id="二、2020年下半年下午题" tabindex="-1">二、2020年下半年下午题 <a class="header-anchor" href="#二、2020年下半年下午题" aria-label="Permalink to &quot;二、2020年下半年下午题&quot;">​</a></h2><h3 id="_1-网络端口与应用程序对应" tabindex="-1">1. 网络端口与应用程序对应 <a class="header-anchor" href="#_1-网络端口与应用程序对应" aria-label="Permalink to &quot;1. 网络端口与应用程序对应&quot;">​</a></h3><p><strong>题目</strong>：在Windows操作系统下，要获取某个网络开放端口所对应的应用程序信息，可以使用什么命令？</p><p><strong>答案</strong>：<code>netstat</code></p><p><strong>命令选项解析</strong>：</p><table tabindex="0"><thead><tr><th>命令</th><th>作用</th></tr></thead><tbody><tr><td><code>ipconfig</code></td><td>显示网络配置信息（IP、子网掩码、网关等）</td></tr><tr><td><code>tracert</code></td><td>追踪数据包路由路径</td></tr><tr><td><code>netstat</code></td><td>显示网络连接、端口、对应进程</td></tr><tr><td><code>nslookup</code></td><td>DNS域名查询</td></tr></tbody></table><p><strong>netstat常用参数</strong>：</p><ul><li><code>netstat -ano</code>：显示所有连接和端口及进程ID</li><li><code>netstat -ab</code>：显示端口对应的程序名（需管理员权限）</li></ul><hr><h2 id="三、高频考点分类" tabindex="-1">三、高频考点分类 <a class="header-anchor" href="#三、高频考点分类" aria-label="Permalink to &quot;三、高频考点分类&quot;">​</a></h2><h3 id="_3-1-计算机网络" tabindex="-1">3.1 计算机网络 <a class="header-anchor" href="#_3-1-计算机网络" aria-label="Permalink to &quot;3.1 计算机网络&quot;">​</a></h3><table tabindex="0"><thead><tr><th>考点</th><th>考查形式</th></tr></thead><tbody><tr><td>TCP/IP协议栈</td><td>各层功能、协议对应</td></tr><tr><td>TCP三次握手</td><td>握手过程、标志位</td></tr><tr><td>子网划分</td><td>子网掩码、网络地址、广播地址计算</td></tr><tr><td>常用端口</td><td>端口号对应协议</td></tr><tr><td>网络命令</td><td>命令功能辨析</td></tr></tbody></table><h3 id="_3-2-软件工程" tabindex="-1">3.2 软件工程 <a class="header-anchor" href="#_3-2-软件工程" aria-label="Permalink to &quot;3.2 软件工程&quot;">​</a></h3><table tabindex="0"><thead><tr><th>考点</th><th>考查形式</th></tr></thead><tbody><tr><td>过程模型</td><td>特点、适用场景选择</td></tr><tr><td>需求工程</td><td>DFD绘制、用例图</td></tr><tr><td>设计模式</td><td>结构识别、场景应用</td></tr><tr><td>测试方法</td><td>白盒黑盒、覆盖标准</td></tr><tr><td>项目管理</td><td>关键路径计算、风险应对</td></tr></tbody></table><h3 id="_3-3-数据库" tabindex="-1">3.3 数据库 <a class="header-anchor" href="#_3-3-数据库" aria-label="Permalink to &quot;3.3 数据库&quot;">​</a></h3><table tabindex="0"><thead><tr><th>考点</th><th>考查形式</th></tr></thead><tbody><tr><td>E-R图</td><td>实体关系识别</td></tr><tr><td>关系模式</td><td>主外键设计</td></tr><tr><td>范式</td><td>1NF/2NF/3NF判断</td></tr><tr><td>SQL语句</td><td>嵌套查询、分组统计</td></tr></tbody></table><hr><h2 id="四、下午题答题技巧" tabindex="-1">四、下午题答题技巧 <a class="header-anchor" href="#四、下午题答题技巧" aria-label="Permalink to &quot;四、下午题答题技巧&quot;">​</a></h2><h3 id="_4-1-数据流图题" tabindex="-1">4.1 数据流图题 <a class="header-anchor" href="#_4-1-数据流图题" aria-label="Permalink to &quot;4.1 数据流图题&quot;">​</a></h3><p><strong>答题步骤</strong>：</p><ol><li>仔细阅读题目描述</li><li>识别外部实体（通常是人或外部系统）</li><li>识别处理/加工（动词描述的活动）</li><li>识别数据存储（名词，如&quot;XX表&quot;、&quot;XX文件&quot;）</li><li>补全数据流（注意命名规范）</li><li>检查父子图平衡</li></ol><p><strong>常见填空类型</strong>：</p><ul><li>补全外部实体</li><li>补全数据存储</li><li>补全数据流名称</li><li>判断父子图是否平衡</li></ul><h3 id="_4-2-数据库设计题" tabindex="-1">4.2 数据库设计题 <a class="header-anchor" href="#_4-2-数据库设计题" aria-label="Permalink to &quot;4.2 数据库设计题&quot;">​</a></h3><p><strong>答题步骤</strong>：</p><ol><li>识别实体和关系</li><li>确定主键和外键</li><li>写SQL语句时注意： <ul><li>SELECT后的字段</li><li>FROM后的表</li><li>WHERE条件</li><li>GROUP BY分组</li><li>HAVING过滤</li></ul></li></ol><h3 id="_4-3-uml-设计模式题" tabindex="-1">4.3 UML/设计模式题 <a class="header-anchor" href="#_4-3-uml-设计模式题" aria-label="Permalink to &quot;4.3 UML/设计模式题&quot;">​</a></h3><p><strong>答题步骤</strong>：</p><ol><li>识别类和它们的职责</li><li>确定类之间的关系</li><li>识别设计模式类型</li><li>补全类图元素</li></ol><p><strong>常见模式识别</strong>：</p><ul><li>工厂模式：创建对象的接口</li><li>单例模式：只有一个实例</li><li>观察者模式：一对多依赖</li><li>策略模式：算法可替换</li><li>适配器模式：接口转换</li></ul><h3 id="_4-4-算法题" tabindex="-1">4.4 算法题 <a class="header-anchor" href="#_4-4-算法题" aria-label="Permalink to &quot;4.4 算法题&quot;">​</a></h3><p><strong>答题步骤</strong>：</p><ol><li>理解算法逻辑</li><li>跟踪变量变化</li><li>注意边界条件</li><li>检查循环条件和返回值</li></ol><hr><h2 id="五、备考建议" tabindex="-1">五、备考建议 <a class="header-anchor" href="#五、备考建议" aria-label="Permalink to &quot;五、备考建议&quot;">​</a></h2><h3 id="_5-1-真题利用方法" tabindex="-1">5.1 真题利用方法 <a class="header-anchor" href="#_5-1-真题利用方法" aria-label="Permalink to &quot;5.1 真题利用方法&quot;">​</a></h3><ol><li><strong>第一遍</strong>：限时模拟，测试真实水平</li><li><strong>第二遍</strong>：精读分析，理解每道题的考点</li><li><strong>第三遍</strong>：错题复盘，强化薄弱环节</li><li><strong>第四遍</strong>：考前速览，巩固记忆</li></ol><h3 id="_5-2-重点年份" tabindex="-1">5.2 重点年份 <a class="header-anchor" href="#_5-2-重点年份" aria-label="Permalink to &quot;5.2 重点年份&quot;">​</a></h3><p>优先练习2017年后的真题（考试改革后题型更稳定）：</p><ul><li>2023年真题（最新）</li><li>2022年真题</li><li>2021年真题</li><li>2020年真题</li><li>2019年真题</li></ul><h3 id="_5-3-时间分配" tabindex="-1">5.3 时间分配 <a class="header-anchor" href="#_5-3-时间分配" aria-label="Permalink to &quot;5.3 时间分配&quot;">​</a></h3><p><strong>上午题</strong>（150分钟，75题）：</p><ul><li>平均每题2分钟</li><li>不确定的题目先标记，最后处理</li></ul><p><strong>下午题</strong>（150分钟，5题）：</p><ul><li>数据流图：25分钟</li><li>数据库设计：30分钟</li><li>UML/设计模式：30分钟</li><li>算法题：35分钟</li><li>程序设计：30分钟</li></ul>',63)]))}const p=a(i,[["render",d]]);export{u as __pageData,p as default};
