import{_ as a,c as n,o as s,a7 as l}from"./chunks/framework.BQb8NfN9.js";const c=JSON.parse('{"title":"软件需求工程","description":"","frontmatter":{},"headers":[],"relativePath":"software-exam/软件设计师/知识点/02-软件需求工程.md","filePath":"software-exam/软件设计师/知识点/02-软件需求工程.md","lastUpdated":1770089353000}'),e={name:"software-exam/软件设计师/知识点/02-软件需求工程.md"};function d(r,t,i,o,p,h){return s(),n("div",null,t[0]||(t[0]=[l(`<h1 id="软件需求工程" tabindex="-1">软件需求工程 <a class="header-anchor" href="#软件需求工程" aria-label="Permalink to &quot;软件需求工程&quot;">​</a></h1><blockquote><p>软件需求工程是软件开发生命周期的起始阶段，核心任务是理解并定义用户对新软件系统的期望和约束。</p></blockquote><hr><h2 id="一、需求工程概述" tabindex="-1">一、需求工程概述 <a class="header-anchor" href="#一、需求工程概述" aria-label="Permalink to &quot;一、需求工程概述&quot;">​</a></h2><h3 id="_1-1-定义与重要性" tabindex="-1">1.1 定义与重要性 <a class="header-anchor" href="#_1-1-定义与重要性" aria-label="Permalink to &quot;1.1 定义与重要性&quot;">​</a></h3><p><strong>软件需求</strong>：从用户角度（系统外部行为）和开发者角度（内部特性）阐述系统必须具备的功能、特性、属性以及约束条件。</p><p><strong>重要性</strong>：</p><ul><li>用户与开发者之间沟通的桥梁</li><li>后续设计、编码、测试和验收的根本依据</li><li>需求分析质量直接关系到项目成败</li></ul><h3 id="_1-2-需求工程的三个维度-phol" tabindex="-1">1.2 需求工程的三个维度（Phol） <a class="header-anchor" href="#_1-2-需求工程的三个维度-phol" aria-label="Permalink to &quot;1.2 需求工程的三个维度（Phol）&quot;">​</a></h3><table tabindex="0"><thead><tr><th>维度</th><th>关注点</th></tr></thead><tbody><tr><td>内容维度</td><td>对系统需求的理解程度</td></tr><tr><td>共识维度</td><td>涉众对已知需求理解的一致性</td></tr><tr><td>文档化维度</td><td>将需求信息有效记录的程度</td></tr></tbody></table><hr><h2 id="二、需求分类" tabindex="-1">二、需求分类 <a class="header-anchor" href="#二、需求分类" aria-label="Permalink to &quot;二、需求分类&quot;">​</a></h2><h3 id="_2-1-按层次分类" tabindex="-1">2.1 按层次分类 <a class="header-anchor" href="#_2-1-按层次分类" aria-label="Permalink to &quot;2.1 按层次分类&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>定义</th><th>关注点</th></tr></thead><tbody><tr><td><strong>业务需求</strong></td><td>组织开发软件的根本原因和预期商业价值</td><td>战略目标</td></tr><tr><td><strong>用户需求</strong></td><td>用户使用系统能完成的具体任务和目标</td><td>用户视角</td></tr><tr><td><strong>系统需求</strong></td><td>对用户需求的细化，详细描述软件服务和约束</td><td>技术实现</td></tr><tr><td><strong>技术需求</strong></td><td>实现系统所需的技术规范、标准、平台</td><td>技术约束</td></tr></tbody></table><h3 id="_2-2-功能需求-vs-非功能需求" tabindex="-1">2.2 功能需求 vs 非功能需求 <a class="header-anchor" href="#_2-2-功能需求-vs-非功能需求" aria-label="Permalink to &quot;2.2 功能需求 vs 非功能需求&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>定义</th><th>示例</th></tr></thead><tbody><tr><td><strong>功能需求</strong></td><td>系统应该做什么</td><td>&quot;系统应允许用户注册账户&quot;</td></tr><tr><td><strong>非功能需求</strong></td><td>系统应该如何工作</td><td>性能、可靠性、安全性、易用性</td></tr></tbody></table><h4 id="常见非功能需求" tabindex="-1">常见非功能需求 <a class="header-anchor" href="#常见非功能需求" aria-label="Permalink to &quot;常见非功能需求&quot;">​</a></h4><table tabindex="0"><thead><tr><th>类型</th><th>描述</th><th>示例</th></tr></thead><tbody><tr><td>性能</td><td>响应时间、吞吐量</td><td>页面加载时间&lt;3秒</td></tr><tr><td>可靠性</td><td>平均无故障时间</td><td>MTBF &gt; 1000小时</td></tr><tr><td>安全性</td><td>数据加密、访问控制</td><td>密码必须加密存储</td></tr><tr><td>易用性</td><td>界面友好、操作便捷</td><td>新用户10分钟内上手</td></tr></tbody></table><hr><h2 id="三、需求过程" tabindex="-1">三、需求过程 <a class="header-anchor" href="#三、需求过程" aria-label="Permalink to &quot;三、需求过程&quot;">​</a></h2><h3 id="_3-1-需求获取-elicitation" tabindex="-1">3.1 需求获取 (Elicitation) <a class="header-anchor" href="#_3-1-需求获取-elicitation" aria-label="Permalink to &quot;3.1 需求获取 (Elicitation)&quot;">​</a></h3><p><strong>目标</strong>：识别需求来源，抽取现有需求，开发创新性需求</p><p><strong>常见困难</strong>：</p><ul><li>用户不清楚自己真正需要什么</li><li>用户难以准确表达需求</li><li>需求随时间变化</li><li>用户和开发者使用不同术语</li><li>开发者倾向于修改需求以适应现有系统</li></ul><p><strong>关键信息持有者（Stakeholders）</strong>：</p><ul><li>直接客户和最终用户</li><li>组织管理层</li><li>与系统有交互的其他部门或系统</li><li>维护人员</li></ul><h3 id="_3-2-需求分析-analysis" tabindex="-1">3.2 需求分析 (Analysis) <a class="header-anchor" href="#_3-2-需求分析-analysis" aria-label="Permalink to &quot;3.2 需求分析 (Analysis)&quot;">​</a></h3><p><strong>目标</strong>：确保需求的合理性、一致性、完整性和可行性</p><p><strong>主要任务</strong>：</p><ul><li>识别并解决需求之间的冲突和矛盾</li><li>确定需求的优先级</li><li>分析需求的可行性</li></ul><h3 id="_3-3-需求规格说明-specification" tabindex="-1">3.3 需求规格说明 (Specification) <a class="header-anchor" href="#_3-3-需求规格说明-specification" aria-label="Permalink to &quot;3.3 需求规格说明 (Specification)&quot;">​</a></h3><p><strong>目标</strong>：将需求清晰、准确、无歧义地记录下来</p><p><strong>产出物</strong>：软件需求规格说明书（SRS）</p><p><strong>SRS的重要性</strong>：</p><ul><li>开发团队进行设计、编码和测试的主要依据</li><li>用户确认系统功能的重要凭证</li></ul><h3 id="_3-4-需求验证-validation" tabindex="-1">3.4 需求验证 (Validation) <a class="header-anchor" href="#_3-4-需求验证-validation" aria-label="Permalink to &quot;3.4 需求验证 (Validation)&quot;">​</a></h3><p><strong>目标</strong>：确保SRS准确反映用户真实意图和期望</p><p><strong>常用方法</strong>：</p><ul><li>评审</li><li>原型演示</li><li>模拟</li></ul><h3 id="_3-5-需求管理-management" tabindex="-1">3.5 需求管理 (Management) <a class="header-anchor" href="#_3-5-需求管理-management" aria-label="Permalink to &quot;3.5 需求管理 (Management)&quot;">​</a></h3><p><strong>目标</strong>：跟踪和控制需求变更</p><p><strong>主要活动</strong>：</p><ul><li>建立变更控制机制</li><li>评估变更影响</li><li>确保相关方对变更达成一致</li></ul><hr><h2 id="四、需求分析方法" tabindex="-1">四、需求分析方法 <a class="header-anchor" href="#四、需求分析方法" aria-label="Permalink to &quot;四、需求分析方法&quot;">​</a></h2><h3 id="_4-1-原型法-prototyping" tabindex="-1">4.1 原型法 (Prototyping) <a class="header-anchor" href="#_4-1-原型法-prototyping" aria-label="Permalink to &quot;4.1 原型法 (Prototyping)&quot;">​</a></h3><p><strong>定义</strong>：通过快速构建可工作的系统模型，帮助理解和澄清需求</p><p><strong>优点</strong>：</p><ul><li>促进用户与开发者沟通</li><li>用户早期看到系统雏形</li><li>尽早发现问题</li></ul><p><strong>缺点</strong>：</p><ul><li>用户可能对原型产生不切实际期望</li><li>临时技术方案可能影响最终质量</li><li>可能忽略非功能需求</li></ul><h3 id="_4-2-用例技术-use-case" tabindex="-1">4.2 用例技术 (Use Case) <a class="header-anchor" href="#_4-2-用例技术-use-case" aria-label="Permalink to &quot;4.2 用例技术 (Use Case)&quot;">​</a></h3><p><strong>定义</strong>：从用户角度描述系统功能的方法</p><h4 id="核心元素" tabindex="-1">核心元素 <a class="header-anchor" href="#核心元素" aria-label="Permalink to &quot;核心元素&quot;">​</a></h4><table tabindex="0"><thead><tr><th>元素</th><th>说明</th><th>图示</th></tr></thead><tbody><tr><td>参与者 (Actor)</td><td>与系统交互的外部实体</td><td>小人图标</td></tr><tr><td>用例 (Use Case)</td><td>系统提供的功能</td><td>椭圆</td></tr><tr><td>关系</td><td>元素之间的联系</td><td>线条</td></tr></tbody></table><h4 id="用例之间的关系" tabindex="-1">用例之间的关系 <a class="header-anchor" href="#用例之间的关系" aria-label="Permalink to &quot;用例之间的关系&quot;">​</a></h4><table tabindex="0"><thead><tr><th>关系</th><th>含义</th><th>符号</th></tr></thead><tbody><tr><td>关联</td><td>参与者与用例的联系</td><td>实线</td></tr><tr><td>包含 (include)</td><td>基用例必须执行的子用例</td><td>虚线箭头 + <code>&lt;&lt;include&gt;&gt;</code></td></tr><tr><td>扩展 (extend)</td><td>可选执行的扩展用例</td><td>虚线箭头 + <code>&lt;&lt;extend&gt;&gt;</code></td></tr><tr><td>泛化</td><td>继承关系</td><td>空心三角箭头</td></tr></tbody></table><blockquote><p><strong>考试要点</strong>：include是必须执行的，extend是可选执行的</p></blockquote><hr><h2 id="五、数据流图-dfd" tabindex="-1">五、数据流图 (DFD) <a class="header-anchor" href="#五、数据流图-dfd" aria-label="Permalink to &quot;五、数据流图 (DFD)&quot;">​</a></h2><h3 id="_5-1-基本概念" tabindex="-1">5.1 基本概念 <a class="header-anchor" href="#_5-1-基本概念" aria-label="Permalink to &quot;5.1 基本概念&quot;">​</a></h3><p>数据流图是结构化分析方法的核心工具，用于对信息系统的数据流和处理过程进行建模。</p><p><strong>特点</strong>：</p><ul><li>描述数据从外部输入，经处理转换，输出或存储的过程</li><li>关注数据的逻辑流动，而非程序控制流程</li></ul><h3 id="_5-2-dfd基本符号" tabindex="-1">5.2 DFD基本符号 <a class="header-anchor" href="#_5-2-dfd基本符号" aria-label="Permalink to &quot;5.2 DFD基本符号&quot;">​</a></h3><table tabindex="0"><thead><tr><th>符号</th><th>名称</th><th>含义</th><th>图形</th></tr></thead><tbody><tr><td>矩形框</td><td>外部实体</td><td>系统边界外的数据源或终点</td><td>□</td></tr><tr><td>圆角矩形/圆形</td><td>处理/加工</td><td>对数据的转换或操作</td><td>○</td></tr><tr><td>两条平行线</td><td>数据存储</td><td>数据的静态存储位置</td><td>═</td></tr><tr><td>带箭头直线</td><td>数据流</td><td>数据流动方向</td><td>→</td></tr></tbody></table><h3 id="_5-3-绘制原则" tabindex="-1">5.3 绘制原则 <a class="header-anchor" href="#_5-3-绘制原则" aria-label="Permalink to &quot;5.3 绘制原则&quot;">​</a></h3><ol><li><strong>清晰易懂</strong>：使用标准符号和简洁命名</li><li><strong>逻辑性</strong>：侧重于数据逻辑流动，非物理实现</li><li><strong>完整性</strong>：包含所有必要元素</li><li><strong>一致性</strong>：整个DFD体系中保持一致</li><li><strong>逐步细化</strong>：自顶向下分层</li></ol><h3 id="_5-4-分层dfd" tabindex="-1">5.4 分层DFD <a class="header-anchor" href="#_5-4-分层dfd" aria-label="Permalink to &quot;5.4 分层DFD&quot;">​</a></h3><table tabindex="0"><thead><tr><th>层次</th><th>名称</th><th>说明</th></tr></thead><tbody><tr><td>顶层图</td><td>上下文图</td><td>单一处理代表整个系统，显示与外部实体的数据交互</td></tr><tr><td>0层图</td><td>系统概览图</td><td>将顶层处理分解为主要子处理</td></tr><tr><td>n层图</td><td>子图</td><td>对上层处理进一步分解</td></tr></tbody></table><h3 id="_5-5-父子图平衡原则-重要" tabindex="-1">5.5 父子图平衡原则（重要！） <a class="header-anchor" href="#_5-5-父子图平衡原则-重要" aria-label="Permalink to &quot;5.5 父子图平衡原则（重要！）&quot;">​</a></h3><p><strong>核心规则</strong>：</p><ul><li>子图输入数据流总和 = 父图对应处理的输入数据流</li><li>子图输出数据流总和 = 父图对应处理的输出数据流</li></ul><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>父图处理P的输入：A, B</span></span>
<span class="line"><span>父图处理P的输出：C</span></span>
<span class="line"><span></span></span>
<span class="line"><span>对应子图：</span></span>
<span class="line"><span>- 输入数据流总和必须等于 A + B</span></span>
<span class="line"><span>- 输出数据流总和必须等于 C</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><h3 id="_5-6-逻辑dfd-vs-物理dfd" tabindex="-1">5.6 逻辑DFD vs 物理DFD <a class="header-anchor" href="#_5-6-逻辑dfd-vs-物理dfd" aria-label="Permalink to &quot;5.6 逻辑DFD vs 物理DFD&quot;">​</a></h3><table tabindex="0"><thead><tr><th>比较维度</th><th>逻辑DFD</th><th>物理DFD</th></tr></thead><tbody><tr><td>描述对象</td><td>业务流程（做什么）</td><td>具体实现（怎么做）</td></tr><tr><td>关注点</td><td>数据逻辑流动</td><td>物理实现方式</td></tr><tr><td>抽象级别</td><td>较高</td><td>较低</td></tr><tr><td>包含元素</td><td>业务活动、逻辑数据单元</td><td>程序模块、物理文件、数据库表</td></tr><tr><td>使用时机</td><td>需求分析早期</td><td>概要/详细设计阶段</td></tr><tr><td>稳定性</td><td>相对稳定</td><td>相对易变</td></tr></tbody></table><h3 id="_5-7-dfd绘制示例" tabindex="-1">5.7 DFD绘制示例 <a class="header-anchor" href="#_5-7-dfd绘制示例" aria-label="Permalink to &quot;5.7 DFD绘制示例&quot;">​</a></h3><p><strong>场景</strong>：图书馆借书系统</p><p><strong>顶层图（上下文图）</strong>：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>  借阅请求                    借阅结果</span></span>
<span class="line"><span>读者 ───────────→ [图书借阅系统] ───────────→ 读者</span></span>
<span class="line"><span>                      ↑</span></span>
<span class="line"><span>                  图书信息</span></span>
<span class="line"><span>                      |</span></span>
<span class="line"><span>                   图书馆</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p><strong>0层图</strong>：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>                    借阅请求</span></span>
<span class="line"><span>读者 ───────────→ [1.0 验证读者] ───读者信息───→ D1读者文件</span></span>
<span class="line"><span>        |                   ↓</span></span>
<span class="line"><span>        |              有效读者信息</span></span>
<span class="line"><span>        |                   ↓</span></span>
<span class="line"><span>        └───图书信息───→ [2.0 检查图书] ←──图书状态──→ D2图书文件</span></span>
<span class="line"><span>                              ↓</span></span>
<span class="line"><span>                         可借图书信息</span></span>
<span class="line"><span>                              ↓</span></span>
<span class="line"><span>                      [3.0 办理借阅] ───借阅记录───→ D3借阅文件</span></span>
<span class="line"><span>                              ↓</span></span>
<span class="line"><span>                          借阅结果</span></span>
<span class="line"><span>                              ↓</span></span>
<span class="line"><span>                            读者</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br></div></div><hr><h2 id="六、考试要点" tabindex="-1">六、考试要点 <a class="header-anchor" href="#六、考试要点" aria-label="Permalink to &quot;六、考试要点&quot;">​</a></h2><h3 id="下午题dfd常见考法" tabindex="-1">下午题DFD常见考法 <a class="header-anchor" href="#下午题dfd常见考法" aria-label="Permalink to &quot;下午题DFD常见考法&quot;">​</a></h3><ol><li><strong>补全数据流</strong>：根据描述补充缺失的数据流</li><li><strong>补全外部实体</strong>：识别系统边界外的实体</li><li><strong>补全数据存储</strong>：识别数据存储位置</li><li><strong>检查平衡性</strong>：验证父子图是否平衡</li><li><strong>纠正错误</strong>：发现并修正DFD中的错误</li></ol><h3 id="答题技巧" tabindex="-1">答题技巧 <a class="header-anchor" href="#答题技巧" aria-label="Permalink to &quot;答题技巧&quot;">​</a></h3><ol><li>仔细阅读题目描述，识别名词（可能是实体或数据存储）</li><li>识别动词（可能是处理/加工）</li><li>检查每个处理是否有输入和输出</li><li>验证父子图平衡</li><li>检查数据流命名是否合理</li></ol><h3 id="典型错误" tabindex="-1">典型错误 <a class="header-anchor" href="#典型错误" aria-label="Permalink to &quot;典型错误&quot;">​</a></h3><ul><li>❌ 数据流没有经过处理直接进入数据存储</li><li>❌ 处理只有输入没有输出</li><li>❌ 处理只有输出没有输入</li><li>❌ 数据流命名不清晰</li><li>❌ 父子图不平衡</li></ul>`,90)]))}const u=a(e,[["render",d]]);export{c as __pageData,u as default};
