import{_ as s,c as n,o as t,a7 as e}from"./chunks/framework.BQb8NfN9.js";const b=JSON.parse('{"title":"软件项目管理","description":"","frontmatter":{},"headers":[],"relativePath":"software-exam/软件设计师/知识点/08-软件项目管理.md","filePath":"software-exam/软件设计师/知识点/08-软件项目管理.md","lastUpdated":1770085161000}'),l={name:"software-exam/软件设计师/知识点/08-软件项目管理.md"};function r(i,a,p,d,o,h){return t(),n("div",null,a[0]||(a[0]=[e(`<h1 id="软件项目管理" tabindex="-1">软件项目管理 <a class="header-anchor" href="#软件项目管理" aria-label="Permalink to &quot;软件项目管理&quot;">​</a></h1><blockquote><p>软件项目管理是运用管理知识、技能、工具和技术于项目活动，以满足项目要求。</p></blockquote><hr><h2 id="一、软件估算" tabindex="-1">一、软件估算 <a class="header-anchor" href="#一、软件估算" aria-label="Permalink to &quot;一、软件估算&quot;">​</a></h2><h3 id="_1-1-代码行估算法-loc" tabindex="-1">1.1 代码行估算法 (LOC) <a class="header-anchor" href="#_1-1-代码行估算法-loc" aria-label="Permalink to &quot;1.1 代码行估算法 (LOC)&quot;">​</a></h3><p><strong>定义</strong>：基于代码行数的估算方法</p><p><strong>公式</strong>：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>工作量 = 代码行数 / 生产率（行/人月）</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><strong>缺点</strong>：</p><ul><li>早期难以准确预测代码行数</li><li>不同语言、风格导致差异大</li><li>不能反映软件复杂度</li></ul><h3 id="_1-2-功能点分析法-fpa" tabindex="-1">1.2 功能点分析法 (FPA) <a class="header-anchor" href="#_1-2-功能点分析法-fpa" aria-label="Permalink to &quot;1.2 功能点分析法 (FPA)&quot;">​</a></h3><p><strong>定义</strong>：从用户角度度量软件功能规模</p><p><strong>特点</strong>：</p><ul><li>不依赖编程语言</li><li>基于用户可见功能</li></ul><h4 id="功能类型" tabindex="-1">功能类型 <a class="header-anchor" href="#功能类型" aria-label="Permalink to &quot;功能类型&quot;">​</a></h4><table tabindex="0"><thead><tr><th>类型</th><th>缩写</th><th>说明</th></tr></thead><tbody><tr><td>内部逻辑文件</td><td>ILF</td><td>系统内部维护的数据组</td></tr><tr><td>外部接口文件</td><td>EIF</td><td>其他系统维护，本系统引用的数据组</td></tr><tr><td>外部输入</td><td>EI</td><td>从外部接收数据，维护ILF</td></tr><tr><td>外部输出</td><td>EO</td><td>向外部发送处理后的数据</td></tr><tr><td>外部查询</td><td>EQ</td><td>检索ILF/EIF向用户提供信息</td></tr></tbody></table><h4 id="计算步骤" tabindex="-1">计算步骤 <a class="header-anchor" href="#计算步骤" aria-label="Permalink to &quot;计算步骤&quot;">​</a></h4><ol><li><strong>识别功能类型</strong></li><li><strong>确定复杂度</strong>（低、中、高）</li><li><strong>计算未调整功能点 (UFP)</strong></li><li><strong>评估14个一般系统特性</strong></li><li><strong>计算技术复杂度因子 (TCF)</strong><ul><li>TCF = 0.65 + (0.01 × DI)</li><li>DI = 14个特性评分之和</li></ul></li><li><strong>计算调整后功能点 (AFP)</strong><ul><li>AFP = UFP × TCF</li></ul></li></ol><h3 id="_1-3-cocomo模型" tabindex="-1">1.3 COCOMO模型 <a class="header-anchor" href="#_1-3-cocomo模型" aria-label="Permalink to &quot;1.3 COCOMO模型&quot;">​</a></h3><p><strong>定义</strong>：构造性成本模型，算法型成本估算</p><h4 id="cocomo-ii-三个子模型" tabindex="-1">COCOMO II 三个子模型 <a class="header-anchor" href="#cocomo-ii-三个子模型" aria-label="Permalink to &quot;COCOMO II 三个子模型&quot;">​</a></h4><table tabindex="0"><thead><tr><th>子模型</th><th>适用阶段</th><th>规模度量</th></tr></thead><tbody><tr><td>应用组装模型</td><td>原型开发</td><td>对象点</td></tr><tr><td>早期设计模型</td><td>早期设计</td><td>功能点</td></tr><tr><td>后架构模型</td><td>实际开发</td><td>代码行(SLOC)</td></tr></tbody></table><h4 id="后架构模型公式" tabindex="-1">后架构模型公式 <a class="header-anchor" href="#后架构模型公式" aria-label="Permalink to &quot;后架构模型公式&quot;">​</a></h4><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>PM = A × (KSLOC)^E × ∏(EM_i)</span></span>
<span class="line"><span></span></span>
<span class="line"><span>其中：</span></span>
<span class="line"><span>- PM：工作量（人月）</span></span>
<span class="line"><span>- A：常数</span></span>
<span class="line"><span>- KSLOC：千行源代码</span></span>
<span class="line"><span>- E：指数（由5个尺度因子决定）</span></span>
<span class="line"><span>- EM_i：17个成本驱动因子</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h4 id="_5个尺度因子-scale-factors" tabindex="-1">5个尺度因子 (Scale Factors) <a class="header-anchor" href="#_5个尺度因子-scale-factors" aria-label="Permalink to &quot;5个尺度因子 (Scale Factors)&quot;">​</a></h4><table tabindex="0"><thead><tr><th>因子</th><th>缩写</th><th>说明</th></tr></thead><tbody><tr><td>先例性</td><td>PREC</td><td>组织对该类项目的熟悉程度</td></tr><tr><td>开发灵活性</td><td>FLEX</td><td>开发过程的灵活程度</td></tr><tr><td>架构/风险化解</td><td>RESL</td><td>风险分析和架构定义程度</td></tr><tr><td>团队凝聚力</td><td>TEAM</td><td>团队协作程度</td></tr><tr><td>过程成熟度</td><td>PMAT</td><td>组织的CMMI级别</td></tr></tbody></table><h4 id="_17个成本驱动因子类别" tabindex="-1">17个成本驱动因子类别 <a class="header-anchor" href="#_17个成本驱动因子类别" aria-label="Permalink to &quot;17个成本驱动因子类别&quot;">​</a></h4><table tabindex="0"><thead><tr><th>类别</th><th>因子示例</th></tr></thead><tbody><tr><td>产品属性</td><td>可靠性要求(RELY)、数据库规模(DATA)、产品复杂性(CPLX)</td></tr><tr><td>平台属性</td><td>执行时间约束(TIME)、主存约束(STOR)</td></tr><tr><td>人员属性</td><td>分析员能力(ACAP)、程序员能力(PCAP)</td></tr><tr><td>项目属性</td><td>工具使用(TOOL)、进度约束(SCED)</td></tr></tbody></table><hr><h2 id="二、项目进度计划" tabindex="-1">二、项目进度计划 <a class="header-anchor" href="#二、项目进度计划" aria-label="Permalink to &quot;二、项目进度计划&quot;">​</a></h2><h3 id="_2-1-工作分解结构-wbs" tabindex="-1">2.1 工作分解结构 (WBS) <a class="header-anchor" href="#_2-1-工作分解结构-wbs" aria-label="Permalink to &quot;2.1 工作分解结构 (WBS)&quot;">​</a></h3><p><strong>定义</strong>：将项目可交付成果分解成更小、更易管理的组成部分</p><p><strong>作用</strong>：</p><ul><li>进度计划基础</li><li>资源分配依据</li><li>成本估算基础</li></ul><h3 id="_2-2-甘特图-gantt-chart" tabindex="-1">2.2 甘特图 (Gantt Chart) <a class="header-anchor" href="#_2-2-甘特图-gantt-chart" aria-label="Permalink to &quot;2.2 甘特图 (Gantt Chart)&quot;">​</a></h3><p><strong>定义</strong>：用条形图显示项目活动的时间安排</p><p><strong>特点</strong>：</p><ul><li>简单直观</li><li>易于理解和制作</li><li>便于跟踪进度</li></ul><p><strong>缺点</strong>：</p><ul><li>难以表示复杂依赖关系</li><li>不能直观显示关键路径</li></ul><h3 id="_2-3-pert图" tabindex="-1">2.3 PERT图 <a class="header-anchor" href="#_2-3-pert图" aria-label="Permalink to &quot;2.3 PERT图&quot;">​</a></h3><p><strong>定义</strong>：基于事件的网络图，使用概率时间估算</p><h4 id="三点估算法" tabindex="-1">三点估算法 <a class="header-anchor" href="#三点估算法" aria-label="Permalink to &quot;三点估算法&quot;">​</a></h4><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>期望时间 = (最乐观时间 + 4×最可能时间 + 最悲观时间) / 6</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><strong>适用场景</strong>：活动持续时间不确定的研发项目</p><h3 id="_2-4-关键路径法-cpm" tabindex="-1">2.4 关键路径法 (CPM) <a class="header-anchor" href="#_2-4-关键路径法-cpm" aria-label="Permalink to &quot;2.4 关键路径法 (CPM)&quot;">​</a></h3><p><strong>定义</strong>：确定项目最短完成时间和关键活动的网络分析技术</p><h4 id="核心概念" tabindex="-1">核心概念 <a class="header-anchor" href="#核心概念" aria-label="Permalink to &quot;核心概念&quot;">​</a></h4><table tabindex="0"><thead><tr><th>概念</th><th>缩写</th><th>定义</th></tr></thead><tbody><tr><td>最早开始时间</td><td>ES</td><td>活动能开始的最早时间</td></tr><tr><td>最早完成时间</td><td>EF</td><td>活动能完成的最早时间</td></tr><tr><td>最迟开始时间</td><td>LS</td><td>不影响工期的最晚开始时间</td></tr><tr><td>最迟完成时间</td><td>LF</td><td>不影响工期的最晚完成时间</td></tr><tr><td>总浮动时间</td><td>TF</td><td>不影响总工期可延迟的时间</td></tr><tr><td>自由浮动时间</td><td>FF</td><td>不影响后续活动可延迟的时间</td></tr></tbody></table><h4 id="计算公式" tabindex="-1">计算公式 <a class="header-anchor" href="#计算公式" aria-label="Permalink to &quot;计算公式&quot;">​</a></h4><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>EF = ES + 持续时间</span></span>
<span class="line"><span>LS = LF - 持续时间</span></span>
<span class="line"><span>TF = LS - ES = LF - EF</span></span>
<span class="line"><span>FF = Min(后续活动ES) - 本活动EF</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h4 id="计算步骤-1" tabindex="-1">计算步骤 <a class="header-anchor" href="#计算步骤-1" aria-label="Permalink to &quot;计算步骤&quot;">​</a></h4><p><strong>正向推导</strong>（计算ES、EF）：</p><ol><li>起始活动 ES = 0</li><li>EF = ES + 持续时间</li><li>后续活动 ES = Max(所有前置活动的EF)</li></ol><p><strong>反向推导</strong>（计算LS、LF）：</p><ol><li>结束活动 LF = 项目工期（或其EF）</li><li>LS = LF - 持续时间</li><li>前置活动 LF = Min(所有后续活动的LS)</li></ol><p><strong>关键路径</strong>：</p><ul><li>总浮动时间 = 0 的活动</li><li>连接所有关键活动的路径</li><li>决定项目最短工期</li></ul><h4 id="计算示例" tabindex="-1">计算示例 <a class="header-anchor" href="#计算示例" aria-label="Permalink to &quot;计算示例&quot;">​</a></h4><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>活动网络图：</span></span>
<span class="line"><span>A(3) → B(4) → D(2)</span></span>
<span class="line"><span>  ↓           ↑</span></span>
<span class="line"><span>C(5) ─────────┘</span></span>
<span class="line"><span></span></span>
<span class="line"><span>活动 | 持续时间 | 前置</span></span>
<span class="line"><span>-----|----------|------</span></span>
<span class="line"><span>A    | 3        | -</span></span>
<span class="line"><span>B    | 4        | A</span></span>
<span class="line"><span>C    | 5        | A</span></span>
<span class="line"><span>D    | 2        | B,C</span></span>
<span class="line"><span></span></span>
<span class="line"><span>正向计算：</span></span>
<span class="line"><span>A: ES=0, EF=3</span></span>
<span class="line"><span>B: ES=3, EF=7</span></span>
<span class="line"><span>C: ES=3, EF=8</span></span>
<span class="line"><span>D: ES=Max(7,8)=8, EF=10</span></span>
<span class="line"><span></span></span>
<span class="line"><span>反向计算：</span></span>
<span class="line"><span>D: LF=10, LS=8</span></span>
<span class="line"><span>B: LF=8, LS=4</span></span>
<span class="line"><span>C: LF=8, LS=3</span></span>
<span class="line"><span>A: LF=Min(4,3)=3, LS=0</span></span>
<span class="line"><span></span></span>
<span class="line"><span>浮动时间：</span></span>
<span class="line"><span>A: TF=0-0=0 (关键)</span></span>
<span class="line"><span>B: TF=4-3=1</span></span>
<span class="line"><span>C: TF=3-3=0 (关键)</span></span>
<span class="line"><span>D: TF=8-8=0 (关键)</span></span>
<span class="line"><span></span></span>
<span class="line"><span>关键路径：A → C → D</span></span>
<span class="line"><span>项目工期：10</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br></div></div><hr><h2 id="三、风险管理" tabindex="-1">三、风险管理 <a class="header-anchor" href="#三、风险管理" aria-label="Permalink to &quot;三、风险管理&quot;">​</a></h2><h3 id="_3-1-风险管理过程" tabindex="-1">3.1 风险管理过程 <a class="header-anchor" href="#_3-1-风险管理过程" aria-label="Permalink to &quot;3.1 风险管理过程&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>风险识别 → 风险分析 → 风险应对 → 风险监控</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_3-2-风险识别" tabindex="-1">3.2 风险识别 <a class="header-anchor" href="#_3-2-风险识别" aria-label="Permalink to &quot;3.2 风险识别&quot;">​</a></h3><p><strong>方法</strong>：</p><ul><li>头脑风暴</li><li>德尔菲技术</li><li>检查表法</li><li>SWOT分析</li><li>经验教训总结</li></ul><h3 id="_3-3-风险分析" tabindex="-1">3.3 风险分析 <a class="header-anchor" href="#_3-3-风险分析" aria-label="Permalink to &quot;3.3 风险分析&quot;">​</a></h3><p><strong>定性分析</strong>：</p><ul><li>风险矩阵法</li><li>评估概率和影响</li><li>优先级排序</li></ul><p><strong>定量分析</strong>：</p><ul><li>蒙特卡洛模拟</li><li>决策树分析</li><li>敏感性分析</li></ul><h3 id="_3-4-风险应对策略" tabindex="-1">3.4 风险应对策略 <a class="header-anchor" href="#_3-4-风险应对策略" aria-label="Permalink to &quot;3.4 风险应对策略&quot;">​</a></h3><h4 id="消极风险-威胁-应对" tabindex="-1">消极风险（威胁）应对 <a class="header-anchor" href="#消极风险-威胁-应对" aria-label="Permalink to &quot;消极风险（威胁）应对&quot;">​</a></h4><table tabindex="0"><thead><tr><th>策略</th><th>说明</th><th>示例</th></tr></thead><tbody><tr><td><strong>规避</strong></td><td>改变计划消除风险</td><td>取消高风险功能</td></tr><tr><td><strong>转移</strong></td><td>将风险转移给第三方</td><td>购买保险、外包</td></tr><tr><td><strong>减轻</strong></td><td>降低概率或影响</td><td>增加测试、培训</td></tr><tr><td><strong>接受</strong></td><td>不采取措施</td><td>设立应急储备</td></tr></tbody></table><h4 id="积极风险-机遇-应对" tabindex="-1">积极风险（机遇）应对 <a class="header-anchor" href="#积极风险-机遇-应对" aria-label="Permalink to &quot;积极风险（机遇）应对&quot;">​</a></h4><table tabindex="0"><thead><tr><th>策略</th><th>说明</th></tr></thead><tbody><tr><td><strong>开拓</strong></td><td>确保机遇实现</td></tr><tr><td><strong>分享</strong></td><td>与第三方分享</td></tr><tr><td><strong>增强</strong></td><td>提高概率或影响</td></tr><tr><td><strong>接受</strong></td><td>不采取特别措施</td></tr></tbody></table><hr><h2 id="四、配置管理" tabindex="-1">四、配置管理 <a class="header-anchor" href="#四、配置管理" aria-label="Permalink to &quot;四、配置管理&quot;">​</a></h2><h3 id="_4-1-基本概念" tabindex="-1">4.1 基本概念 <a class="header-anchor" href="#_4-1-基本概念" aria-label="Permalink to &quot;4.1 基本概念&quot;">​</a></h3><p><strong>定义</strong>：识别、组织、控制和跟踪软件配置项修改的技术和规程</p><p><strong>目的</strong>：保持软件产品的完整性、一致性和可追溯性</p><h3 id="_4-2-核心概念" tabindex="-1">4.2 核心概念 <a class="header-anchor" href="#_4-2-核心概念" aria-label="Permalink to &quot;4.2 核心概念&quot;">​</a></h3><table tabindex="0"><thead><tr><th>概念</th><th>定义</th></tr></thead><tbody><tr><td><strong>配置项 (CI)</strong></td><td>需要单独识别和控制的工作产品</td></tr><tr><td><strong>基线 (Baseline)</strong></td><td>特定时间点上经过评审批准的配置项版本</td></tr><tr><td><strong>版本控制</strong></td><td>管理配置项的不同版本</td></tr><tr><td><strong>变更控制</strong></td><td>控制对基线的修改</td></tr></tbody></table><h3 id="_4-3-常见配置项" tabindex="-1">4.3 常见配置项 <a class="header-anchor" href="#_4-3-常见配置项" aria-label="Permalink to &quot;4.3 常见配置项&quot;">​</a></h3><ul><li>需求规格说明书</li><li>设计文档</li><li>源代码</li><li>测试计划和用例</li><li>可执行代码</li><li>用户手册</li></ul><h3 id="_4-4-常见基线" tabindex="-1">4.4 常见基线 <a class="header-anchor" href="#_4-4-常见基线" aria-label="Permalink to &quot;4.4 常见基线&quot;">​</a></h3><table tabindex="0"><thead><tr><th>基线</th><th>建立时机</th></tr></thead><tbody><tr><td>需求基线</td><td>需求评审通过后</td></tr><tr><td>设计基线</td><td>设计评审通过后</td></tr><tr><td>代码基线</td><td>代码评审通过后</td></tr><tr><td>测试基线</td><td>测试完成后</td></tr><tr><td>产品基线</td><td>产品发布时</td></tr></tbody></table><h3 id="_4-5-变更控制流程" tabindex="-1">4.5 变更控制流程 <a class="header-anchor" href="#_4-5-变更控制流程" aria-label="Permalink to &quot;4.5 变更控制流程&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>提交变更请求 → 评估影响 → 审批 → 实施变更 → 验证 → 更新配置记录</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_4-6-配置审计" tabindex="-1">4.6 配置审计 <a class="header-anchor" href="#_4-6-配置审计" aria-label="Permalink to &quot;4.6 配置审计&quot;">​</a></h3><p><strong>目的</strong>：</p><ul><li>验证配置管理活动的符合性</li><li>确保配置项的完整性和一致性</li><li>确保变更得到正确记录和批准</li></ul><hr><h2 id="五、考试要点" tabindex="-1">五、考试要点 <a class="header-anchor" href="#五、考试要点" aria-label="Permalink to &quot;五、考试要点&quot;">​</a></h2><h3 id="高频考点" tabindex="-1">高频考点 <a class="header-anchor" href="#高频考点" aria-label="Permalink to &quot;高频考点&quot;">​</a></h3><ol><li><strong>关键路径计算</strong>（ES、EF、LS、LF、浮动时间）</li><li><strong>功能点分析法基本概念</strong></li><li><strong>COCOMO II模型结构</strong></li><li><strong>风险应对策略</strong></li><li><strong>配置管理核心概念</strong></li></ol><h3 id="典型例题" tabindex="-1">典型例题 <a class="header-anchor" href="#典型例题" aria-label="Permalink to &quot;典型例题&quot;">​</a></h3><p><strong>题1</strong>：某活动ES=5，持续时间=3，则EF=？ <strong>答案</strong>：EF = 5 + 3 = 8</p><p><strong>题2</strong>：某活动LF=10，LS=7，EF=8，ES=5，则TF=？ <strong>答案</strong>：TF = LS - ES = 7 - 5 = 2（或 LF - EF = 10 - 8 = 2）</p><p><strong>题3</strong>：将风险转移给保险公司，这属于什么风险应对策略？ <strong>答案</strong>：转移</p><p><strong>题4</strong>：需求规格说明书属于什么？ <strong>答案</strong>：配置项</p><h3 id="关键路径计算步骤" tabindex="-1">关键路径计算步骤 <a class="header-anchor" href="#关键路径计算步骤" aria-label="Permalink to &quot;关键路径计算步骤&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>1. 画出活动网络图</span></span>
<span class="line"><span>2. 正向计算ES和EF（从前往后）</span></span>
<span class="line"><span>3. 确定项目工期（最后一个活动的EF）</span></span>
<span class="line"><span>4. 反向计算LS和LF（从后往前）</span></span>
<span class="line"><span>5. 计算TF = LS - ES</span></span>
<span class="line"><span>6. TF = 0 的活动为关键活动</span></span>
<span class="line"><span>7. 连接关键活动得到关键路径</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h3 id="记忆口诀" tabindex="-1">记忆口诀 <a class="header-anchor" href="#记忆口诀" aria-label="Permalink to &quot;记忆口诀&quot;">​</a></h3><p><strong>关键路径</strong>：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>正向求早反向迟</span></span>
<span class="line"><span>早完等于早开加时</span></span>
<span class="line"><span>迟开等于迟完减时</span></span>
<span class="line"><span>浮动为零是关键</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p><strong>风险应对</strong>：</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>消极风险规避转移减轻接受</span></span>
<span class="line"><span>积极机遇开拓分享增强接受</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div>`,109)]))}const u=s(l,[["render",r]]);export{b as __pageData,u as default};
