import{_ as a,c as s,o as n,a7 as d}from"./chunks/framework.BQb8NfN9.js";const b=JSON.parse('{"title":"计算机网络基础","description":"","frontmatter":{},"headers":[],"relativePath":"software-exam/软件设计师/知识点/09-计算机网络基础.md","filePath":"software-exam/软件设计师/知识点/09-计算机网络基础.md","lastUpdated":1770085161000}'),e={name:"software-exam/软件设计师/知识点/09-计算机网络基础.md"};function r(l,t,i,p,o,h){return n(),s("div",null,t[0]||(t[0]=[d(`<h1 id="计算机网络基础" tabindex="-1">计算机网络基础 <a class="header-anchor" href="#计算机网络基础" aria-label="Permalink to &quot;计算机网络基础&quot;">​</a></h1><blockquote><p>计算机网络是软件设计师考试的重要考点，主要考查网络协议、IP地址计算和常用网络命令。</p></blockquote><hr><h2 id="一、网络体系结构" tabindex="-1">一、网络体系结构 <a class="header-anchor" href="#一、网络体系结构" aria-label="Permalink to &quot;一、网络体系结构&quot;">​</a></h2><h3 id="_1-1-osi七层模型" tabindex="-1">1.1 OSI七层模型 <a class="header-anchor" href="#_1-1-osi七层模型" aria-label="Permalink to &quot;1.1 OSI七层模型&quot;">​</a></h3><table tabindex="0"><thead><tr><th>层次</th><th>名称</th><th>功能</th><th>协议/设备</th></tr></thead><tbody><tr><td>7</td><td>应用层</td><td>提供应用服务</td><td>HTTP, FTP, SMTP, DNS</td></tr><tr><td>6</td><td>表示层</td><td>数据格式转换、加密</td><td>SSL, JPEG</td></tr><tr><td>5</td><td>会话层</td><td>建立、管理会话</td><td>RPC</td></tr><tr><td>4</td><td>传输层</td><td>端到端传输</td><td>TCP, UDP</td></tr><tr><td>3</td><td>网络层</td><td>路由寻址</td><td>IP, ICMP, 路由器</td></tr><tr><td>2</td><td>数据链路层</td><td>帧传输</td><td>交换机, MAC</td></tr><tr><td>1</td><td>物理层</td><td>比特传输</td><td>网卡, 中继器, 集线器</td></tr></tbody></table><h3 id="_1-2-tcp-ip四层模型" tabindex="-1">1.2 TCP/IP四层模型 <a class="header-anchor" href="#_1-2-tcp-ip四层模型" aria-label="Permalink to &quot;1.2 TCP/IP四层模型&quot;">​</a></h3><table tabindex="0"><thead><tr><th>TCP/IP层</th><th>对应OSI层</th><th>协议</th></tr></thead><tbody><tr><td>应用层</td><td>应用层+表示层+会话层</td><td>HTTP, FTP, DNS, SMTP</td></tr><tr><td>传输层</td><td>传输层</td><td>TCP, UDP</td></tr><tr><td>网络层</td><td>网络层</td><td>IP, ICMP, ARP</td></tr><tr><td>网络接口层</td><td>数据链路层+物理层</td><td>Ethernet</td></tr></tbody></table><hr><h2 id="二、常用协议" tabindex="-1">二、常用协议 <a class="header-anchor" href="#二、常用协议" aria-label="Permalink to &quot;二、常用协议&quot;">​</a></h2><h3 id="_2-1-tcp-vs-udp" tabindex="-1">2.1 TCP vs UDP <a class="header-anchor" href="#_2-1-tcp-vs-udp" aria-label="Permalink to &quot;2.1 TCP vs UDP&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特性</th><th>TCP</th><th>UDP</th></tr></thead><tbody><tr><td>连接性</td><td>面向连接</td><td>无连接</td></tr><tr><td>可靠性</td><td>可靠传输</td><td>不可靠</td></tr><tr><td>传输方式</td><td>字节流</td><td>数据报</td></tr><tr><td>速度</td><td>较慢</td><td>较快</td></tr><tr><td>应用场景</td><td>文件传输、网页</td><td>视频、游戏</td></tr></tbody></table><h3 id="_2-2-tcp三次握手" tabindex="-1">2.2 TCP三次握手 <a class="header-anchor" href="#_2-2-tcp三次握手" aria-label="Permalink to &quot;2.2 TCP三次握手&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>客户端                    服务器</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |------ SYN(seq=x) -----&gt;|  第一次握手</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |&lt;-- SYN+ACK(seq=y,ack=x+1) --|  第二次握手</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |------ ACK(ack=y+1) ---&gt;|  第三次握手</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |      连接建立成功       |</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><p><strong>为什么需要三次握手</strong>：</p><ul><li>防止失效的连接请求到达服务器</li><li>确认双方的接收和发送能力</li></ul><h3 id="_2-3-tcp四次挥手" tabindex="-1">2.3 TCP四次挥手 <a class="header-anchor" href="#_2-3-tcp四次挥手" aria-label="Permalink to &quot;2.3 TCP四次挥手&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>客户端                    服务器</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |------ FIN(seq=u) -----&gt;|  第一次挥手</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |&lt;----- ACK(ack=u+1) ----|  第二次挥手</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |      (服务器发送数据)    |</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |&lt;----- FIN(seq=w) ------|  第三次挥手</span></span>
<span class="line"><span>   |                        |</span></span>
<span class="line"><span>   |------ ACK(ack=w+1) ---&gt;|  第四次挥手</span></span>
<span class="line"><span>   |                        |</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br></div></div><h3 id="_2-4-常用端口号" tabindex="-1">2.4 常用端口号 <a class="header-anchor" href="#_2-4-常用端口号" aria-label="Permalink to &quot;2.4 常用端口号&quot;">​</a></h3><table tabindex="0"><thead><tr><th>协议</th><th>端口</th><th>说明</th></tr></thead><tbody><tr><td>FTP控制</td><td>21</td><td>文件传输控制</td></tr><tr><td>FTP数据</td><td>20</td><td>文件传输数据</td></tr><tr><td>SSH</td><td>22</td><td>安全远程登录</td></tr><tr><td>Telnet</td><td>23</td><td>远程登录</td></tr><tr><td>SMTP</td><td>25</td><td>邮件发送</td></tr><tr><td>DNS</td><td>53</td><td>域名解析</td></tr><tr><td>HTTP</td><td>80</td><td>网页</td></tr><tr><td>POP3</td><td>110</td><td>邮件接收</td></tr><tr><td>HTTPS</td><td>443</td><td>安全网页</td></tr></tbody></table><h3 id="_2-5-http状态码" tabindex="-1">2.5 HTTP状态码 <a class="header-anchor" href="#_2-5-http状态码" aria-label="Permalink to &quot;2.5 HTTP状态码&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类别</th><th>范围</th><th>说明</th><th>常见状态码</th></tr></thead><tbody><tr><td>1xx</td><td>100-199</td><td>信息性</td><td>100 Continue</td></tr><tr><td>2xx</td><td>200-299</td><td>成功</td><td>200 OK, 201 Created</td></tr><tr><td>3xx</td><td>300-399</td><td>重定向</td><td>301 永久重定向, 302 临时重定向</td></tr><tr><td>4xx</td><td>400-499</td><td>客户端错误</td><td>400 Bad Request, 404 Not Found</td></tr><tr><td>5xx</td><td>500-599</td><td>服务器错误</td><td>500 Internal Error, 503 Service Unavailable</td></tr></tbody></table><hr><h2 id="三、ip地址" tabindex="-1">三、IP地址 <a class="header-anchor" href="#三、ip地址" aria-label="Permalink to &quot;三、IP地址&quot;">​</a></h2><h3 id="_3-1-ip地址分类" tabindex="-1">3.1 IP地址分类 <a class="header-anchor" href="#_3-1-ip地址分类" aria-label="Permalink to &quot;3.1 IP地址分类&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类别</th><th>第一字节范围</th><th>网络号位数</th><th>主机号位数</th><th>默认子网掩码</th></tr></thead><tbody><tr><td>A类</td><td>1-126</td><td>8</td><td>24</td><td>255.0.0.0</td></tr><tr><td>B类</td><td>128-191</td><td>16</td><td>16</td><td>255.255.0.0</td></tr><tr><td>C类</td><td>192-223</td><td>24</td><td>8</td><td>255.255.255.0</td></tr><tr><td>D类</td><td>224-239</td><td>组播地址</td><td>-</td><td>-</td></tr><tr><td>E类</td><td>240-255</td><td>保留</td><td>-</td><td>-</td></tr></tbody></table><h3 id="_3-2-特殊ip地址" tabindex="-1">3.2 特殊IP地址 <a class="header-anchor" href="#_3-2-特殊ip地址" aria-label="Permalink to &quot;3.2 特殊IP地址&quot;">​</a></h3><table tabindex="0"><thead><tr><th>地址</th><th>说明</th></tr></thead><tbody><tr><td>127.0.0.1</td><td>本地回环地址</td></tr><tr><td>0.0.0.0</td><td>任意地址</td></tr><tr><td>255.255.255.255</td><td>广播地址</td></tr><tr><td>10.x.x.x</td><td>A类私有地址</td></tr><tr><td>172.16.x.x - 172.31.x.x</td><td>B类私有地址</td></tr><tr><td>192.168.x.x</td><td>C类私有地址</td></tr></tbody></table><h3 id="_3-3-子网划分" tabindex="-1">3.3 子网划分 <a class="header-anchor" href="#_3-3-子网划分" aria-label="Permalink to &quot;3.3 子网划分&quot;">​</a></h3><h4 id="cidr表示法" tabindex="-1">CIDR表示法 <a class="header-anchor" href="#cidr表示法" aria-label="Permalink to &quot;CIDR表示法&quot;">​</a></h4><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>192.168.1.0/24 表示前24位是网络号</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h4 id="子网掩码计算" tabindex="-1">子网掩码计算 <a class="header-anchor" href="#子网掩码计算" aria-label="Permalink to &quot;子网掩码计算&quot;">​</a></h4><p><strong>示例</strong>：192.168.1.100/26</p><ol><li>/26 表示网络位26位，主机位6位</li><li>子网掩码：11111111.11111111.11111111.11000000 = 255.255.255.192</li><li>子网大小：2^6 = 64个地址</li><li>可用主机数：64 - 2 = 62个（去掉网络地址和广播地址）</li></ol><h4 id="计算网络地址" tabindex="-1">计算网络地址 <a class="header-anchor" href="#计算网络地址" aria-label="Permalink to &quot;计算网络地址&quot;">​</a></h4><p><strong>步骤</strong>：IP地址 AND 子网掩码</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>IP地址：     192.168.1.100</span></span>
<span class="line"><span>子网掩码：   255.255.255.192</span></span>
<span class="line"><span></span></span>
<span class="line"><span>192.168.1.100 = 11000000.10101000.00000001.01100100</span></span>
<span class="line"><span>255.255.255.192 = 11111111.11111111.11111111.11000000</span></span>
<span class="line"><span>                  ─────────────────────────────────</span></span>
<span class="line"><span>网络地址：        11000000.10101000.00000001.01000000</span></span>
<span class="line"><span>                = 192.168.1.64</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h4 id="计算广播地址" tabindex="-1">计算广播地址 <a class="header-anchor" href="#计算广播地址" aria-label="Permalink to &quot;计算广播地址&quot;">​</a></h4><p><strong>步骤</strong>：网络地址 + (2^主机位数 - 1)</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>网络地址：192.168.1.64</span></span>
<span class="line"><span>主机位：6位</span></span>
<span class="line"><span>广播地址：192.168.1.64 + 63 = 192.168.1.127</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h4 id="计算默认网关" tabindex="-1">计算默认网关 <a class="header-anchor" href="#计算默认网关" aria-label="Permalink to &quot;计算默认网关&quot;">​</a></h4><p><strong>通常</strong>：网络中第一个可用地址</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>网络地址：192.168.1.64</span></span>
<span class="line"><span>默认网关：192.168.1.65（网络地址+1）</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><hr><h2 id="四、常用网络命令" tabindex="-1">四、常用网络命令 <a class="header-anchor" href="#四、常用网络命令" aria-label="Permalink to &quot;四、常用网络命令&quot;">​</a></h2><h3 id="_4-1-ping命令" tabindex="-1">4.1 PING命令 <a class="header-anchor" href="#_4-1-ping命令" aria-label="Permalink to &quot;4.1 PING命令&quot;">​</a></h3><p><strong>用途</strong>：测试网络连通性</p><p><strong>使用ICMP协议</strong></p><p><strong>诊断顺序</strong>（由近到远）：</p><ol><li><code>ping 127.0.0.1</code> - 测试本地TCP/IP协议栈</li><li><code>ping 本地IP</code> - 测试本地网卡</li><li><code>ping 默认网关</code> - 测试到网关的连通</li><li><code>ping 远程主机</code> - 测试到远程的连通</li></ol><h3 id="_4-2-常用命令" tabindex="-1">4.2 常用命令 <a class="header-anchor" href="#_4-2-常用命令" aria-label="Permalink to &quot;4.2 常用命令&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>用途</th></tr></thead><tbody><tr><td><strong>ping</strong></td><td>测试网络连通性</td></tr><tr><td><strong>ipconfig</strong> (Windows)</td><td>显示网络配置信息</td></tr><tr><td><strong>ifconfig</strong> (Linux)</td><td>显示网络配置信息</td></tr><tr><td><strong>tracert</strong> (Windows)</td><td>追踪路由路径</td></tr><tr><td><strong>traceroute</strong> (Linux)</td><td>追踪路由路径</td></tr><tr><td><strong>netstat</strong></td><td>显示网络连接状态和端口</td></tr><tr><td><strong>nslookup</strong></td><td>DNS查询</td></tr><tr><td><strong>arp</strong></td><td>查看ARP缓存</td></tr></tbody></table><h3 id="_4-3-netstat命令" tabindex="-1">4.3 netstat命令 <a class="header-anchor" href="#_4-3-netstat命令" aria-label="Permalink to &quot;4.3 netstat命令&quot;">​</a></h3><p><strong>用途</strong>：显示网络连接、端口使用情况</p><p><strong>常用参数</strong>：</p><ul><li><code>netstat -a</code>：显示所有连接和端口</li><li><code>netstat -n</code>：以数字形式显示地址和端口</li><li><code>netstat -o</code>：显示进程ID</li><li><code>netstat -b</code>：显示程序名称（需管理员权限）</li></ul><p><strong>查看端口对应程序</strong>：<code>netstat -ano</code> 或 <code>netstat -ab</code></p><hr><h2 id="五、加密技术" tabindex="-1">五、加密技术 <a class="header-anchor" href="#五、加密技术" aria-label="Permalink to &quot;五、加密技术&quot;">​</a></h2><h3 id="_5-1-对称加密-vs-非对称加密" tabindex="-1">5.1 对称加密 vs 非对称加密 <a class="header-anchor" href="#_5-1-对称加密-vs-非对称加密" aria-label="Permalink to &quot;5.1 对称加密 vs 非对称加密&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特性</th><th>对称加密</th><th>非对称加密</th></tr></thead><tbody><tr><td>密钥</td><td>加密解密用同一密钥</td><td>公钥加密，私钥解密</td></tr><tr><td>速度</td><td>快</td><td>慢</td></tr><tr><td>安全性</td><td>密钥分发困难</td><td>较安全</td></tr><tr><td>典型算法</td><td>DES, AES, 3DES</td><td>RSA, ECC</td></tr></tbody></table><h3 id="_5-2-常见加密算法" tabindex="-1">5.2 常见加密算法 <a class="header-anchor" href="#_5-2-常见加密算法" aria-label="Permalink to &quot;5.2 常见加密算法&quot;">​</a></h3><table tabindex="0"><thead><tr><th>算法</th><th>类型</th><th>说明</th></tr></thead><tbody><tr><td>DES</td><td>对称</td><td>56位密钥，已不安全</td></tr><tr><td>3DES</td><td>对称</td><td>三重DES</td></tr><tr><td>AES</td><td>对称</td><td>128/192/256位密钥</td></tr><tr><td>RSA</td><td>非对称</td><td>基于大数因子分解</td></tr><tr><td>MD5</td><td>摘要</td><td>128位摘要，已不安全</td></tr><tr><td>SHA</td><td>摘要</td><td>SHA-1 160位，SHA-256 256位</td></tr></tbody></table><h3 id="_5-3-数字签名" tabindex="-1">5.3 数字签名 <a class="header-anchor" href="#_5-3-数字签名" aria-label="Permalink to &quot;5.3 数字签名&quot;">​</a></h3><p><strong>过程</strong>：</p><ol><li>发送方用<strong>私钥</strong>对消息摘要加密（签名）</li><li>接收方用<strong>公钥</strong>验证签名</li></ol><p><strong>作用</strong>：</p><ul><li>认证：确认发送者身份</li><li>完整性：确保消息未被篡改</li><li>不可否认性：发送者不能否认发送</li></ul><hr><h2 id="六、考试要点" tabindex="-1">六、考试要点 <a class="header-anchor" href="#六、考试要点" aria-label="Permalink to &quot;六、考试要点&quot;">​</a></h2><h3 id="高频考点" tabindex="-1">高频考点 <a class="header-anchor" href="#高频考点" aria-label="Permalink to &quot;高频考点&quot;">​</a></h3><ol><li><strong>TCP三次握手过程</strong></li><li><strong>常用端口号</strong></li><li><strong>IP地址分类和子网计算</strong></li><li><strong>网络命令用途</strong></li><li><strong>对称加密vs非对称加密</strong></li></ol><h3 id="典型例题" tabindex="-1">典型例题 <a class="header-anchor" href="#典型例题" aria-label="Permalink to &quot;典型例题&quot;">​</a></h3><p><strong>题1</strong>：要获取某网络端口对应的应用程序信息，应使用什么命令？ <strong>答案</strong>：netstat</p><p><strong>题2</strong>：192.168.1.100/28的子网掩码是？ <strong>答案</strong>：255.255.255.240（/28表示28位网络位）</p><p><strong>题3</strong>：FTP的控制连接端口是？ <strong>答案</strong>：21</p><p><strong>题4</strong>：RSA属于什么加密算法？ <strong>答案</strong>：非对称加密</p><h3 id="ping诊断顺序记忆" tabindex="-1">PING诊断顺序记忆 <a class="header-anchor" href="#ping诊断顺序记忆" aria-label="Permalink to &quot;PING诊断顺序记忆&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>本回环 → 本地IP → 默认网关 → 远程主机</span></span>
<span class="line"><span>127.0.0.1 → 本机 → 网关 → 目标</span></span>
<span class="line"><span>由近及远排查问题</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><h3 id="常用端口记忆" tabindex="-1">常用端口记忆 <a class="header-anchor" href="#常用端口记忆" aria-label="Permalink to &quot;常用端口记忆&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>21-FTP控制，20-FTP数据</span></span>
<span class="line"><span>22-SSH安全，23-Telnet登</span></span>
<span class="line"><span>25-SMTP发邮件，53-DNS解析名</span></span>
<span class="line"><span>80-HTTP网页，443-HTTPS安全访</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div>`,81)]))}const u=a(e,[["render",r]]);export{b as __pageData,u as default};
