import{_ as d,c as r,o as a,a7 as e}from"./chunks/framework.BQb8NfN9.js";const c=JSON.parse('{"title":"软考-软件设计师 考试结构与重点","description":"","frontmatter":{},"headers":[],"relativePath":"software-exam/软件设计师/考试指南/01-考试结构与重点.md","filePath":"software-exam/软件设计师/考试指南/01-考试结构与重点.md","lastUpdated":1770085161000}'),h={name:"software-exam/软件设计师/考试指南/01-考试结构与重点.md"};function o(l,t,n,i,s,b){return a(),r("div",null,t[0]||(t[0]=[e('<h1 id="软考-软件设计师-考试结构与重点" tabindex="-1">软考-软件设计师 考试结构与重点 <a class="header-anchor" href="#软考-软件设计师-考试结构与重点" aria-label="Permalink to &quot;软考-软件设计师 考试结构与重点&quot;">​</a></h1><h2 id="一、考试概述" tabindex="-1">一、考试概述 <a class="header-anchor" href="#一、考试概述" aria-label="Permalink to &quot;一、考试概述&quot;">​</a></h2><p>软件设计师是全国计算机技术与软件专业技术资格（水平）考试的中级资格，考试分为上午和下午两场。</p><hr><h2 id="二、考试科目详解" tabindex="-1">二、考试科目详解 <a class="header-anchor" href="#二、考试科目详解" aria-label="Permalink to &quot;二、考试科目详解&quot;">​</a></h2><h3 id="_1-上午题-综合知识" tabindex="-1">1. 上午题（综合知识） <a class="header-anchor" href="#_1-上午题-综合知识" aria-label="Permalink to &quot;1. 上午题（综合知识）&quot;">​</a></h3><table tabindex="0"><thead><tr><th>项目</th><th>说明</th></tr></thead><tbody><tr><td>题型</td><td>75道选择题</td></tr><tr><td>满分</td><td>75分</td></tr><tr><td>时长</td><td>150分钟</td></tr><tr><td>合格线</td><td>45分</td></tr></tbody></table><p><strong>各知识点分值占比：</strong></p><table tabindex="0"><thead><tr><th>模块</th><th>占比</th><th>主要内容</th></tr></thead><tbody><tr><td>软件工程基础</td><td>~17%</td><td>开发模型（瀑布、敏捷）、测试方法、质量特性（ISO 9126）、CMM、Pert图、风险管理</td></tr><tr><td>面向对象</td><td>~15%</td><td>面向对象基本概念、UML图（类图、用例图）、设计模式（单例、工厂模式等）</td></tr><tr><td>数据结构与算法</td><td>~12%</td><td>数组、栈、队列、树、图、排序算法、查找算法、动态规划</td></tr><tr><td>计算机硬件基础</td><td>~8%</td><td>浮点数运算、流水线加速比、Cache计算、存储编址</td></tr><tr><td>操作系统</td><td>~8%</td><td>进程状态转换、PV操作、死锁（银行家算法）、页面置换算法、磁盘调度算法</td></tr><tr><td>数据库系统</td><td>~7%</td><td>E-R模型、关系模式设计、SQL语句、规范化（第三范式）</td></tr><tr><td>计算机网络</td><td>~7%</td><td>TCP/IP协议（三次握手）、子网划分（CIDR表示法）、网络设备配置</td></tr><tr><td>其他</td><td>~20%</td><td>程序设计语言、信息安全、多媒体、文法与有限自动机、加密技术</td></tr></tbody></table><hr><h3 id="_2-下午题-应用技术" tabindex="-1">2. 下午题（应用技术） <a class="header-anchor" href="#_2-下午题-应用技术" aria-label="Permalink to &quot;2. 下午题（应用技术）&quot;">​</a></h3><table tabindex="0"><thead><tr><th>项目</th><th>说明</th></tr></thead><tbody><tr><td>题型</td><td>6道案例分析题（选做5道）</td></tr><tr><td>满分</td><td>75分</td></tr><tr><td>时长</td><td>150分钟</td></tr><tr><td>合格线</td><td>45分</td></tr></tbody></table><p><strong>题型与分值分布：</strong></p><table tabindex="0"><thead><tr><th>题目</th><th>分值</th><th>内容</th><th>性质</th></tr></thead><tbody><tr><td>数据流图</td><td>~15分</td><td>补全数据流、外实体或数据存储，检查父子图平衡性</td><td>必答</td></tr><tr><td>数据库设计</td><td>~15分</td><td>E-R图转关系模式、主键/外键设计、SQL语句优化</td><td>必答</td></tr><tr><td>UML建模</td><td>~15分</td><td>类图（关联、继承关系）、用例图、活动图或状态图补全</td><td>必答</td></tr><tr><td>C语言算法</td><td>~15分</td><td>链表操作、二叉树遍历、动态规划/分治算法实现</td><td>必答</td></tr><tr><td>编程填空</td><td>~15分</td><td>设计模式应用（如工厂模式）、代码补全（C++/Java二选一）</td><td>选答</td></tr></tbody></table><hr><h2 id="三、高频考点汇总" tabindex="-1">三、高频考点汇总 <a class="header-anchor" href="#三、高频考点汇总" aria-label="Permalink to &quot;三、高频考点汇总&quot;">​</a></h2><h3 id="上午题高频考点" tabindex="-1">上午题高频考点 <a class="header-anchor" href="#上午题高频考点" aria-label="Permalink to &quot;上午题高频考点&quot;">​</a></h3><table tabindex="0"><thead><tr><th>分类</th><th>必背内容</th></tr></thead><tbody><tr><td><strong>设计模式</strong></td><td>工厂模式（解耦对象创建）、观察者模式（发布-订阅）、适配器模式（接口转换）、单例模式、策略模式、装饰器模式</td></tr><tr><td><strong>UML图</strong></td><td>类图（关联/聚合/组合）、时序图（对象生命周期）、用例图（include/extend）</td></tr><tr><td><strong>网络协议</strong></td><td>TCP三次握手流程、HTTP状态码（200/404/500）、HTTPS加密原理</td></tr><tr><td><strong>法律条款</strong></td><td>软件著作权归属（开发者为单位时归单位）、专利权保护期限（20年）</td></tr><tr><td><strong>算法复杂度</strong></td><td>快排平均O(nlogn)、冒泡O(n²)、二叉树查找O(logn)</td></tr><tr><td><strong>操作系统</strong></td><td>PV操作、死锁条件、页面置换（LRU、FIFO）、磁盘调度（SCAN、SSTF）</td></tr></tbody></table><h3 id="下午题解题技巧" tabindex="-1">下午题解题技巧 <a class="header-anchor" href="#下午题解题技巧" aria-label="Permalink to &quot;下午题解题技巧&quot;">​</a></h3><table tabindex="0"><thead><tr><th>题型</th><th>技巧要点</th></tr></thead><tbody><tr><td><strong>数据流图</strong></td><td>①识别外部实体、处理、数据存储 ②检查父子图平衡 ③数据流命名规范</td></tr><tr><td><strong>数据库设计</strong></td><td>①E-R图识别实体和关系 ②关系模式确定主键外键 ③SQL注意嵌套和分组</td></tr><tr><td><strong>UML建模</strong></td><td>①类图关系区分（聚合空心菱形、组合实心菱形） ②用例图include必须执行、extend可选执行</td></tr><tr><td><strong>算法填空</strong></td><td>①理解算法逻辑 ②关注边界条件 ③注意返回值和变量类型</td></tr><tr><td><strong>设计模式</strong></td><td>①识别模式类型 ②理解角色职责 ③补全关键代码</td></tr></tbody></table><hr><h2 id="四、备考优先级矩阵" tabindex="-1">四、备考优先级矩阵 <a class="header-anchor" href="#四、备考优先级矩阵" aria-label="Permalink to &quot;四、备考优先级矩阵&quot;">​</a></h2><table tabindex="0"><thead><tr><th>模块</th><th>上午题占比</th><th>下午题题型</th><th>备考优先级</th></tr></thead><tbody><tr><td>软件工程</td><td>30%</td><td>数据流图/数据库设计</td><td>★★★★★</td></tr><tr><td>面向对象与设计模式</td><td>20%</td><td>UML建模</td><td>★★★★★</td></tr><tr><td>数据库与网络</td><td>20%</td><td>算法与设计模式</td><td>★★★★☆</td></tr><tr><td>计算机基础</td><td>15%</td><td>面向对象程序设计</td><td>★★★☆☆</td></tr><tr><td>法律与标准化</td><td>10%</td><td>-</td><td>★★☆☆☆</td></tr><tr><td>专业英语</td><td>5%</td><td>-</td><td>★★☆☆☆</td></tr></tbody></table><hr><h2 id="五、推荐学习资源" tabindex="-1">五、推荐学习资源 <a class="header-anchor" href="#五、推荐学习资源" aria-label="Permalink to &quot;五、推荐学习资源&quot;">​</a></h2><h3 id="教材" tabindex="-1">教材 <a class="header-anchor" href="#教材" aria-label="Permalink to &quot;教材&quot;">​</a></h3><ul><li>《软件设计师教程（第5版）》— 作字典查阅，无需通读</li></ul><h3 id="视频" tabindex="-1">视频 <a class="header-anchor" href="#视频" aria-label="Permalink to &quot;视频&quot;">​</a></h3><ul><li>B站王勇老师课程</li><li>zst_2001真题解析</li></ul><h3 id="刷题工具" tabindex="-1">刷题工具 <a class="header-anchor" href="#刷题工具" aria-label="Permalink to &quot;刷题工具&quot;">​</a></h3><ul><li>&quot;跨步软考&quot;APP（选择题）</li><li>希赛网、才士题库</li><li>XMind（知识框架图）</li></ul><h3 id="真题" tabindex="-1">真题 <a class="header-anchor" href="#真题" aria-label="Permalink to &quot;真题&quot;">​</a></h3><ul><li>优先刷2017年后真题（2017年考试改革后题型更稳定）</li></ul>',33)]))}const _=d(h,[["render",o]]);export{c as __pageData,_ as default};
